@Override
public Hook create(final String name, final Map<String, String> config, final Iterable<Event> events, final boolean active) throws IOException {
    final JsonObjectBuilder configs = Json.createObjectBuilder();
    for (final Map.Entry<String, String> entr : config.entrySet()) {
        configs.add(entr.getKey(), entr.getValue());
    }
    final JsonArrayBuilder evnts = Json.createArrayBuilder();
    for (final Event event : events) {
        evnts.add(event.toString());
    }
    final JsonStructure json = Json.createObjectBuilder().add("name", name).add("config", configs).add("active", active).add("events", evnts).build();
    return this.get(this.request.method(Request.POST).body().set(json).back().fetch().as(RestResponse.class).assertStatus(HttpURLConnection.HTTP_CREATED).as(JsonResponse.class).json().readObject().getInt("id"));
}

