    for (final Map.Entry<Qualifier, String> entry : qualifiers.entrySet()) {
        keyword.append('+').append(entry.getKey().identifier()).append(':').append(entry.getValue());
    }
    return new RtSearchPagination<>(this.request, "issues", keyword.toString(), sort, order.identifier(), object -> {
        try {
            final String[] parts = // @checkstyle MultipleStringLiteralsCheck (1 line)
            RtSearch.SLASH.split(new URI(object.getString("url")).getPath());
            return // @checkstyle MagicNumber (1 line)
            this.ghub.repos().get(new Coordinates.Simple(parts[2], parts[3])).issues().get(object.getInt("number"));
        } catch (final URISyntaxException ex) {
            throw new IllegalStateException(ex);
        }
    });
}

