@Override
public Iterable<Content> codes(final String keywords, final String sort, final Order order) {
    return // @checkstyle AnonInnerLengthCheck (25 lines)
    new RtSearchPagination<>(this.request, "code", keywords, sort, order.identifier(), object -> {
        try {
            // @checkstyle MultipleStringLiteralsCheck (1 line)
            final URI uri = new URI(object.getString("url"));
            final String[] parts = RtSearch.SLASH.split(uri.getPath());
            final String ref = RtSearch.QUERY.split(uri.getQuery())[1];
            return // @checkstyle MagicNumber (1 line)
            this.ghub.repos().get(new Coordinates.Simple(parts[2], parts[3])).contents().get(object.getString("path"), ref);
        } catch (final URISyntaxException ex) {
            throw new IllegalStateException(ex);
        } catch (final IOException ex) {
            throw new IllegalStateException(ex);
        }
    });
}

