@Override
public PullComment reply(final String body, final int comment) throws IOException {
    this.storage.lock();
    try {
        final JsonObject orig = this.get(comment).json();
        final PullComment reply = this.post(body, orig.getString("commit_id"), orig.getString("path"), comment);
        reply.patch(Json.createObjectBuilder().add("original_position", String.valueOf(comment)).build());
        return reply;
    } finally {
        this.storage.unlock();
    }
}

