@Override
public MergeState merge(final String msg, final String sha) throws IOException {
    final JsonObjectBuilder builder = Json.createObjectBuilder().add("commit_message", msg).add("sha", sha);
    final RestResponse response = this.merge(builder.build()).assertStatus(Matchers.is(Matchers.oneOf(HttpURLConnection.HTTP_OK, HttpURLConnection.HTTP_BAD_METHOD, HttpURLConnection.HTTP_CONFLICT)));
    final MergeState mergeState;
    switch (response.status()) {
        case HttpURLConnection.HTTP_OK :
            mergeState = MergeState.SUCCESS;
            break;
        case HttpURLConnection.HTTP_BAD_METHOD :
            mergeState = MergeState.NOT_MERGEABLE;
            break;
        default :
            mergeState = MergeState.BAD_HEAD;
            break;
    }
    return mergeState;
}

