/**
 * Get all checks.
 * JSON schema:
 * <p>{@code {
 *   "total_count": 1,
 *   "check_runs": [
 *     {
 *       "id": 4,
 *       "status": "completed",
 *       ...
 *     }
 *   ]
 * }}</p>
 *
 * @return Checks.
 * @throws IOException
 * 		If there is any I/O problem.
 */
@Override
public Collection<? extends Check> all() throws IOException {
    final Coordinates coords = this.pull.repo().coordinates();
    final RestResponse rest = this.request.uri().path("/repos").path(coords.user()).path(coords.repo()).path("/commits").path(this.pull.head().sha()).path("/check-runs").back().method(Request.GET).fetch().as(RestResponse.class).assertStatus(HttpURLConnection.HTTP_OK);
    final JsonObject object = rest.as(JsonResponse.class).json().readObject();
    return Optional.ofNullable(object.getJsonArray("check_runs")).map(obj -> obj.stream().map(RtChecks::check).collect(Collectors.toList())).orElseGet(Collections::emptyList);
}

