@Override
public void add(final Iterable<String> labels) throws IOException {
    final Collection<String> existing = this.labels();
    final Set<String> added = new HashSet<>();
    final Directives dirs = new Directives().xpath(this.xpath());
    for (final String label : labels) {
        dirs.add("label").set(label).up();
        if (!existing.contains(label)) {
            added.add(label);
        }
    }
    this.storage.apply(dirs);
    if (!added.isEmpty()) {
        final MkIssueEvents events = new MkIssueEvents(this.storage, this.self, this.repo);
        for (final String label : added) {
            events.create(Event.LABELED, this.ticket, this.self, Optional.of(label));
        }
    }
}

@Override
public void remove(final String name) throws IOException {
    if (this.labels().contains(name)) {
        this.storage.apply(new Directives().xpath(String.format("%s/label[.='%s']", this.xpath(), name)).remove());
        new MkIssueEvents(this.storage, this.self, this.repo).create(Event.UNLABELED, this.ticket, this.self, Optional.of(name));
    }
}

