/**
 * Get all checks.
 * JSON schema:
 * <p>{@code {
 *   "total_count": 1,
 *   "check_runs": [
 *     {
 *       "id": 4,
 *       "status": "completed",
 *       ...
 *     }
 *   ]
 * }}</p>
 *
 * @return Checks.
 * @throws IOException
 * 		If there is any I/O problem.
 */
@Override
public Collection<? extends Check> all() throws IOException {
    final Coordinates coords = this.pull.repo().coordinates();
    final RestResponse rest = this.request.uri().path("/repos").path(coords.user()).path(coords.repo()).path("/commits").path(this.pull.head().sha()).path("/check-runs").back().method(Request.GET).fetch().as(RestResponse.class).assertStatus(HttpURLConnection.HTTP_OK);
    final JsonObject object = rest.as(JsonResponse.class).json().readObject();
    return Optional.ofNullable(object.getJsonArray("check_runs")).map(obj -> obj.stream().map(RtChecks::check).collect(Collectors.toList())).orElseGet(Collections::emptyList);
}

@Override
@SuppressWarnings("PMD.AvoidInstantiatingObjectsInLoops")
public Tree create(final JsonObject params) throws IOException {
    final JsonArray trees = params.getJsonArray("tree");
    for (final JsonValue val : trees) {
        final JsonObject tree = ((JsonObject) (val));
        final String sha = tree.getString("sha");
        final Directives dirs = new Directives().xpath(this.xpath()).add("tree");
        for (final Entry<String, JsonValue> entry : tree.entrySet()) {
            dirs.add(entry.getKey()).set(entry.getValue().toString()).up();
        }
        this.storage.apply(dirs);
        final String ref;
        if (tree.containsValue("name")) {
            ref = tree.getString("name");
        } else {
            ref = sha;
        }
        new MkReferences(this.storage, this.self, this.coords).create(new StringBuilder("refs/trees/").append(ref).toString(), sha);
    }
    return this.get(trees.getJsonObject(0).getString("sha"));
}

