/**
 * Updates a file.
 *
 * @param path
 * 		The content path.
 * @param json
 * 		JSON object containing updates to the content.
 * @return Commit related to this update.
 * @throws IOException
 * 		If any I/O problem occurs.
 */
@Override
public RepoCommit update(final String path, final JsonObject json) throws IOException {
    this.storage.lock();
    try {
        final String ref = "ref";
        final String branch;
        if (json.containsKey(ref)) {
            branch = json.getString(ref);
        } else {
            branch = "master";
        }
        final String xpath = // @checkstyle LineLengthCheck (1 line)
        String.format("/github/repos/repo[@coords='%s']/contents/content[path='%s' and @ref='%s']", this.coords, path, branch);
        new JsonPatch(this.storage).patch(xpath, json);
        return this.commit(json);
    } finally {
        this.storage.unlock();
    }
}

