        this.storage.apply(new Directives().xpath(this.xpath()).add("comment").add("number").set(Long.toString(number)).up().add("url").set(// @checkstyle LineLength (1 line)
        String.format("https://api.jcabi-github.invalid/repos/%s/%s/issues/comments/%d", this.repo.user(), this.repo.repo(), number)).up().add("body").set(text).up().add("user").add("login").set(this.self).up().up().add("created_at").set(timestamp).up().add("updated_at").set(timestamp));
    } finally {
        this.storage.unlock();
    }
    Logger.info(this, "comment #%d posted to issue #%d by %s: %[text]s", number, this.issue().number(), this.self, text);
    return this.get(number);
