@Override
public Repo create(final RepoCreate settings) throws IOException {
    String owner = this.self;
    final String org = settings.organization();
    if ((org != null) && (!org.isEmpty())) {
        owner = "/orgs/".concat(org).concat("/repos");
    }
    final Coordinates coords = new Coordinates.Simple(owner, settings.name());
    this.storage.apply(new Directives().xpath(this.xpath()).add("repo").attr("coords", coords.toString()).add("name").set(settings.name()).up().add("description").set("test repository").up().add("private").set(settings.isPrivate()).up());
    final Repo repo = this.get(coords);
    repo.patch(settings.json());
    Logger.info(this, "repository %s created by %s", coords, owner);
    return repo;
}

// @checkstyle ParameterNumber (8 lines)
@Override
public Response send(final Request req, final String home, final String method, final Collection<Map.Entry<String, String>> headers, final InputStream content, final int connect, final int read) throws IOException {
    final Response resp = this.origin.send(req, home, method, headers, content, connect, read);
    final int remaining = this.remainingHeader(resp);
    if (remaining < this.threshold) {
        final long reset = this.resetHeader(resp);
        final long now = TimeUnit.MILLISECONDS.toSeconds(System.currentTimeMillis());
        if (reset > now) {
            final long length = reset - now;
            // @checkstyle LineLength (1 line)
            Logger.info(this, "Remaining number of requests per hour is less than %d. Waiting for %d seconds.", this.threshold, length);
            try {
                TimeUnit.SECONDS.sleep(length);
            } catch (final InterruptedException ex) {
                Thread.currentThread().interrupt();
                throw new IllegalStateException(ex);
            }
        }
    }
    return resp;
}

