// @checkstyle ParameterNumber (8 lines)
@Override
public Response send(final Request req, final String home, final String method, final Collection<Map.Entry<String, String>> headers, final InputStream content, final int connect, final int read) throws IOException {
    final Response resp = this.origin.send(req, home, method, headers, content, connect, read);
    final int remaining = this.remainingHeader(resp);
    if (remaining < this.threshold) {
        final long reset = this.resetHeader(resp);
        final long now = TimeUnit.MILLISECONDS.toSeconds(System.currentTimeMillis());
        if (reset > now) {
            final long length = reset - now;
            // @checkstyle LineLength (1 line)
            Logger.info(this, "Remaining number of requests per hour is less than %d. Waiting for %d seconds.", this.threshold, length);
            try {
                TimeUnit.SECONDS.sleep(length);
            } catch (final InterruptedException ex) {
                Thread.currentThread().interrupt();
                throw new IllegalStateException(ex);
            }
        }
    }
    return resp;
}

@Override
public Iterable<Content> iterate(final String pattern, final String ref) throws IOException {
    final Collection<XML> nodes = this.storage.xml().nodes(String.format("%s/content[@ref='%s']", this.xpath(), ref));
    final Collection<Content> result = new ArrayList<>(nodes.size());
    for (final XML node : nodes) {
        final String path = node.xpath("path/text()").get(0);
        if (path.startsWith(pattern)) {
            result.add(this.mkContent(ref, path));
        }
    }
    return result;
}

/**
 * Fetch JSON object.
 *
 * @return JSON
 * @checkstyle MultipleStringLiteralsCheck (30 lines)
 */
@SuppressWarnings("PMD.AvoidInstantiatingObjectsInLoops")
public JsonObject json() {
    final JsonObjectBuilder builder = Json.createObjectBuilder();
    for (final XML child : this.xml.nodes("* ")) {
        final Node node = child.node();
        if (child.nodes("*").isEmpty()) {
            builder.add(node.getNodeName(), node.getTextContent());
        } else if ((!child.xpath("//@array").isEmpty()) && "true".equals(child.xpath("//@array").get(0))) {
            final JsonArrayBuilder bld = Json.createArrayBuilder();
            for (final XML item : child.nodes("*")) {
                bld.add(item.node().getTextContent());
            }
            builder.add(node.getNodeName(), bld.build());
        } else {
            builder.add(node.getNodeName(), new JsonNode(child).json());
        }
    }
    return builder.build();
}

