public Milestone create(final String title) throws IOException {
    final int number;
    number = 1 + this.storage.xml().xpath(String.format("%s/milestone/number/text()", this.xpath())).size();
    this.storage.apply(new Directives().xpath(this.xpath()).add("milestone").add("number").set(Integer.toString(number)).up().add("title").set(title).up().add("state").set(Milestone.OPEN_STATE).up().add("description").set("mock milestone").up());
    return this.get(number);
}

@Override
public Issue create(final String title, final String body) throws IOException {
    this.storage.lock();
    final int number;
    try {
        number = 1 + this.storage.xml().xpath(String.format("%s/issue/number/text()", this.xpath())).size();
        this.storage.apply(new Directives().xpath(this.xpath()).add("issue").add("number").set(Integer.toString(number)).up().add("state").set(Issue.OPEN_STATE).up().add("title").set(title).up().add("body").set(body).up().add("created_at").set(new Github.Time().toString()).up().add("updated_at").set(new Github.Time().toString()).up().add("url").set("http://localhost/1").up().add("html_url").set("http://localhost/2").up().add("user").add("login").set(this.self).up().up());
    } finally {
        this.storage.unlock();
    }
    Logger.info(this, "issue #%d created in %s by %s: %[text]s", number, this.repo().coordinates(), this.self, title);
    return this.get(number);
}

