@Override
public JsonObject json() throws IOException {
    final XML xml = this.storage.xml().nodes(this.xpath()).get(0);
    final String branch = xml.xpath("base/text()").get(0);
    final String head = xml.xpath("head/text()").get(0);
    final String[] parts = head.split(MkPull.USER_BRANCH_SEP, 2);
    final JsonObject obj = new JsonNode(xml).json();
    final JsonObjectBuilder json = Json.createObjectBuilder();
    for (final Map.Entry<String, JsonValue> val : obj.entrySet()) {
        if (MkPull.NUMBER_PROP.equals(val.getKey())) {
            json.add(MkPull.NUMBER_PROP, Integer.parseInt(xml.xpath("number/text()").get(0)));
        } else if (MkPull.USER_PROP.equals(val.getKey())) {
            json.add(MkPull.USER_PROP, Json.createObjectBuilder().add("login", xml.xpath("user/login/text()").get(0)).build());
        } else if (MkPull.HEAD_PROP.equals(val.getKey())) {
            json.add(MkPull.HEAD_PROP, Json.createObjectBuilder().add(MkPull.REF_PROP, parts[1]).add(MkPull.LABEL_PROP, head).build());
        } else if (MkPull.BASE_PROP.equals(val.getKey())) {
            json.add(MkPull.BASE_PROP, Json.createObjectBuilder().add(MkPull.REF_PROP, branch).add(MkPull.LABEL_PROP, String.format("%s:%s", this.coords.user(), branch)).build());
        } else {
            json.add(val.getKey(), val.getValue());
        }
    }
    json.add("comments", this.storage.xml().nodes(this.comment()).size());
    return json.build();
}

