/**
 * Get its property as custom type.
 *
 * @param name
 * 		Name of the property
 * @param type
 * 		Type of result expected
 * @return Value
 * @throws IOException
 * 		If there is any I/O problem
 * @param <T>
 * 		Type expected
 */
public <T> T value(final String name, final Class<T> type) throws IOException {
    final JsonObject json = this.json();
    if (!json.containsKey(name)) {
        throw new IllegalStateException(String.format("'%s' is absent in JSON: %s", name, json));
    }
    final JsonValue value = json.get(name);
    if (value == null) {
        throw new IllegalStateException(String.format("'%s' is NULL in %s", name, json));
    }
    if (value.getClass().isAssignableFrom(type)) {
        throw new IllegalStateException(String.format("'%s' is not of type %s", name, type));
    }
    return type.cast(value);
}

