@Override
@SuppressWarnings("PMD.AvoidInstantiatingObjectsInLoops")
public Tree create(final JsonObject params) throws IOException {
    final JsonArray trees = params.getJsonArray("tree");
    for (final JsonValue val : trees) {
        final JsonObject tree = ((JsonObject) (val));
        final String sha = tree.getString("sha");
        final Directives dirs = new Directives().xpath(this.xpath()).add("tree");
        for (final Entry<String, JsonValue> entry : tree.entrySet()) {
            dirs.add(entry.getKey()).set(entry.getValue().toString()).up();
        }
        this.storage.apply(dirs);
        final String ref;
        if (tree.containsValue("name")) {
            ref = tree.getString("name");
        } else {
            ref = sha;
        }
        new MkReferences(this.storage, this.self, this.coords).create(new StringBuilder("refs/trees/").append(ref).toString(), sha);
    }
    return this.get(trees.getJsonObject(0).getString("sha"));
}

// @checkstyle ParameterNumberCheck (2 lines)
@Override
public Hook create(final String name, final Map<String, String> config, final Iterable<Event> events, final boolean active) throws IOException {
    this.storage.lock();
    final int number;
    try {
        number = 1 + this.storage.xml().xpath(String.format("%s/hook/id/text()", this.xpath())).size();
        final Directives dirs = new Directives().xpath(this.xpath()).add("hook").add("id").set(String.valueOf(number)).up().add("name").set(name).up().add("active").set(Boolean.toString(active)).up().add("events");
        for (final Event event : events) {
            dirs.add("event").set(event.toString()).up();
        }
        dirs.up().add("config");
        for (final Map.Entry<String, String> entr : config.entrySet()) {
            dirs.add(entr.getKey()).set(entr.getValue()).up();
        }
        this.storage.apply(dirs);
    } finally {
        this.storage.unlock();
    }
    return this.get(number);
}

