@Override
public void patch(final JsonObject json) throws IOException {
    final Issue.Smart smart = new Issue.Smart(this);
    final boolean was = smart.isOpen();
    new JsonPatch(this.storage).patch(this.xpath(), json);
    final boolean now = smart.isOpen();
    if (now != was) {
        final String type;
        if (now) {
            type = Event.REOPENED;
        } else {
            type = Event.CLOSED;
        }
        new MkIssueEvents(this.storage, this.self, this.coords).create(type, this.num, this.self, Optional.<String>absent());
    }
}

