 */
public Optional<Label> label() throws IOException {
    Optional<Label> lab = Optional.absent();
    final JsonObject lbl = this.jsn.json().getJsonObject("label");
    if (lbl != null) {
        lab = Optional.of(this.event.repo().labels().get(lbl.getString("name")));
