public String addEmbeddData(byte[] embeddData, String contentType, String part) throws InvalidFormatException, IOException {
    PackagePartName partName = PackagingURIHelper.createPartName(part);
    PackagePart packagePart = getPackage().createPart(partName, contentType);
    try (OutputStream out = packagePart.getOutputStream()) {
        out.write(embeddData);
    } catch (IOException e) {
        throw new POIXMLException(e);
    }
    PackageRelationship ole = getPackagePart().addRelationship(partName, TargetMode.INTERNAL, POIXMLDocument.PACK_OBJECT_REL_TYPE);
    return ole.getId();
}

public String addEmbeddData(byte[] embeddData, int format) throws InvalidFormatException {
    XWPFRelation relation = (0 == format) ? DOCUMENT : XWPFRelation.WORKBOOK;
    int idx = 256 + getRelationIndex(relation);
    POIXMLDocumentPart embeddPart = createRelationship(relation, XWPFFactory.getInstance(), idx);
    embedds.add(embeddPart);
    /* write bytes to new part */
    PackagePart picDataPart = embeddPart.getPackagePart();
    try (OutputStream out = picDataPart.getOutputStream()) {
        out.write(embeddData);
    } catch (IOException e) {
        throw new POIXMLException(e);
    }
    return getRelationId(embeddPart);
}

@Override
public NiceXWPFDocument merge(NiceXWPFDocument source, Iterator<NiceXWPFDocument> mergeIterator, XWPFRun run) throws Exception {
    CTBody body = source.getDocument().getBody();
    List<String> addParts = createMergeableStrings(source, mergeIterator);
    String[] startEnd = truncatedStartEndXmlFragment(body);
    XWPFParagraph paragraph = ((XWPFParagraph) (run.getParent()));
    CTP mergedContainer = paragraph.getCTP();
    CTP mergedBody = CTP.Factory.parse((((startEnd[0] + "<w:POITL>") + String.join("", addParts)) + "</w:POITL>") + startEnd[1]);
    // instead insert xml-fragment?
    mergedContainer.set(mergedBody);
    String xmlText = truncatedOverlapWP(body);
    body.set(CTBody.Factory.parse(xmlText));
    return source.generate(true);
}

