@Override
public List<MetaTemplate> resolveBodyElements(List<IBodyElement> bodyElements) {
    List<MetaTemplate> metaTemplates = new ArrayList<>();
    if (null == bodyElements)
        return metaTemplates;

    // current iterable templates state
    Deque<BlockTemplate> stack = new LinkedList<BlockTemplate>();
    for (IBodyElement element : bodyElements) {
        if (element == null)
            continue;

        if (element.getElementType() == BodyElementType.PARAGRAPH) {
            resolveParagraph(((XWPFParagraph) (element)), metaTemplates, stack);
        } else if (element.getElementType() == BodyElementType.TABLE) {
            XWPFTable table = ((XWPFTable) (element));
            List<XWPFTableRow> rows = table.getRows();
            if (null == rows)
                continue;

            for (XWPFTableRow row : rows) {
                resolveTableRow(row, metaTemplates, stack);
            }
        } else if ((element.getElementType() == BodyElementType.CONTENTCONTROL) && (element instanceof XWPFStructuredDocumentTag)) {
            XWPFStructuredDocumentTag sdt = ((XWPFStructuredDocumentTag) (element));
            addNewMeta(metaTemplates, stack, resolveSDTElements(sdt.getContent()));
        }
    }
    checkStack(stack);
    return metaTemplates;
}

@Override
public List<MetaTemplate> resolveXWPFRuns(List<XWPFRun> runs) {
    List<MetaTemplate> metaTemplates = new ArrayList<>();
    if (runs == null)
        return metaTemplates;

    Deque<BlockTemplate> stack = new LinkedList<BlockTemplate>();
    resolveXWPFRuns(runs, metaTemplates, stack);
    checkStack(stack);
    return metaTemplates;
}

public void resolveParagraph(XWPFParagraph paragraph, List<MetaTemplate> metaTemplates, Deque<BlockTemplate> stack) {
    XWPFParagraphWrapper paragraphWrapper = new XWPFParagraphWrapper(paragraph);
    new RunningRunBody(new ParagraphContext(paragraphWrapper), templatePattern).refactorRun();
    resolveXWPFRuns(paragraph.getRuns(), metaTemplates, stack);
    paragraphWrapper.getSDTs().forEach(sdtEle -> addNewMeta(metaTemplates, stack, resolveSDTElements(sdtEle.getContent())));
}

public XWPFRun insertNewHyperLinkRun(XWPFRun run, String link) {
    if (StringUtils.isBlank(link))
        throw new IllegalArgumentException("Hyperlink must not be Empty!");

    if (link.startsWith("anchor:")) {
        return insertNewAnchor(getPosOfRun(run), link.substring("anchor:".length()));
    }
    return insertNewHyperLinkRun(getPosOfRun(run), link);
}

@Override
public NiceXWPFDocument merge(NiceXWPFDocument source, Iterator<NiceXWPFDocument> mergeIterator, XWPFRun run) throws Exception {
    CTBody body = source.getDocument().getBody();
    List<String> addParts = createMergeableStrings(source, mergeIterator);
    String[] startEnd = truncatedStartEndXmlFragment(body);
    XWPFParagraph paragraph = ((XWPFParagraph) (run.getParent()));
    CTP mergedContainer = paragraph.getCTP();
    CTP mergedBody = CTP.Factory.parse((((startEnd[0] + "<w:POITL>") + String.join("", addParts)) + "</w:POITL>") + startEnd[1]);
    // instead insert xml-fragment?
    mergedContainer.set(mergedBody);
    String xmlText = truncatedOverlapWP(body);
    body.set(CTBody.Factory.parse(xmlText));
    return source.generate(true);
}

public String getChartId() {
    CTGraphicalObjectData graphicData = null;
    if (ctDrawing.sizeOfAnchorArray() > 0) {
        CTAnchor anchorArray = ctDrawing.getAnchorArray(0);
        graphicData = anchorArray.getGraphic().getGraphicData();
    } else if (ctDrawing.sizeOfInlineArray() > 0) {
        CTInline inline = ctDrawing.getInlineArray(0);
        graphicData = inline.getGraphic().getGraphicData();
    }
    XmlCursor newCursor = graphicData.newCursor();
    try {
        boolean child = newCursor.toChild(0);
        if (!child)
            return null;

        XmlObject xmlObject = newCursor.getObject();
        if ((null == xmlObject) || (!(xmlObject instanceof CTRelId)))
            return null;

        CTRelId cchart = ((CTRelId) (xmlObject));
        return cchart.getId();
    } finally {
        newCursor.dispose();
    }
}

