public static void renderTextRun(XWPFRun run, TextRenderData data) {
    XWPFRun textRun = run;
    if (data instanceof HyperlinkTextRenderData) {
        textRun = createHyperlink(run, ((HyperlinkTextRenderData) (data)).getUrl());
    }
    StyleUtils.styleRun(textRun, data.getStyle());
    String text = Optional.ofNullable(data.getText()).orElse("");
    String[] fragment = text.split(REGEX_LINE_CHARACTOR, -1);
    if (fragment.length > 0) {
        textRun.setText(fragment[0], 0);
        boolean lineAtTable = ((fragment.length > 1) && (!(data instanceof HyperlinkTextRenderData))) && TableTools.isInsideTable(run);
        for (int i = 1; i < fragment.length; i++) {
            if (lineAtTable) {
                textRun.addBreak(BreakType.TEXT_WRAPPING);
            } else {
                textRun.addCarriageReturn();
            }
            textRun.setText(fragment[i]);
        }
    }
    if (data instanceof BookmarkTextRenderData) {
        createBookmark(textRun, ((BookmarkTextRenderData) (data)).getBookmark());
    }
}

@Override
public XWPFParagraph insertNewParagraph(XmlCursor cursor) {
    if (isCursorInBody(cursor)) {
        String uri = CTP.type.getName().getNamespaceURI();
        /* TODO DO not use a coded constant, find the constant in the OOXML
        classes instead, as the child of type CT_Paragraph is defined in the
        OOXML schema as 'p'
         */
        String localPart = "p";
        // creates a new Paragraph, cursor is positioned inside the new
        // element
        cursor.beginElement(localPart, uri);
        // move the cursor to the START token to the paragraph just created
        cursor.toParent();
        CTP p = ((CTP) (cursor.getObject()));
        XWPFParagraph newP = new XWPFParagraph(p, this);
        XmlObject o = null;
        /* move the cursor to the previous element until a) the next
        paragraph is found or b) all elements have been passed
         */
        while ((!(o instanceof CTP)) && cursor.toPrevSibling()) {
            o = cursor.getObject();
        } 
        /* if the object that has been found is a) not a paragraph or b) is
        the paragraph that has just been inserted, as the cursor in the
        while loop above was not moved as there were no other siblings,
        then the paragraph that was just inserted is the first paragraph
        in the body. Otherwise, take the previous paragraph and calculate
        the new index for the new paragraph.
         */
        if ((!(o instanceof CTP)) || (o == p)) {
            paragraphs.add(0, newP);
        } else {
            int pos = paragraphs.indexOf(getParagraph(((CTP) (o)))) + 1;
            paragraphs.add(pos, newP);
        }
        /* create a new cursor, that points to the START token of the just
        inserted paragraph
         */
        XmlCursor newParaPos = p.newCursor();
        try {
            /* Calculate the paragraphs index in the list of all body
            elements
             */
            int i = 0;
            cursor.toCursor(newParaPos);
            while (cursor.toPrevSibling()) {
                o = cursor.getObject();
                if (((o instanceof CTP) || (o instanceof CTTbl)) || (o instanceof CTSdtBlock)) {
                    i++;
                }
            } 
            bodyElements.add(i, newP);
            cursor.toCursor(newParaPos);
            cursor.toEndToken();
            return newP;
        } finally {
            newParaPos.dispose();
        }
    }
    return null;
}

/**
 * add a new paragraph at position of the cursor
 *
 * @param cursor
 * 		The XmlCursor structure created with XmlBeans
 * @return the inserted paragraph
 */
@Override
public XWPFParagraph insertNewParagraph(final XmlCursor cursor) {
    if (!isCursorInTextBox(cursor)) {
        return null;
    }
    String uri = CTP.type.getName().getNamespaceURI();
    String localPart = "p";
    cursor.beginElement(localPart, uri);
    cursor.toParent();
    CTP p = ((CTP) (cursor.getObject()));
    XWPFParagraph newP = new XWPFParagraph(p, this);
    XmlObject o = null;
    while ((!(o instanceof CTP)) && cursor.toPrevSibling()) {
        o = cursor.getObject();
    } 
    if ((!(o instanceof CTP)) || (o == p)) {
        paragraphs.add(0, newP);
    } else {
        int pos = paragraphs.indexOf(getParagraph(((CTP) (o)))) + 1;
        paragraphs.add(pos, newP);
    }
    int i = 0;
    XmlCursor p2 = p.newCursor();
    cursor.toCursor(p2);
    p2.dispose();
    while (cursor.toPrevSibling()) {
