public XWPFRun insertNewRunAfter(int pos) {
    if (pos == runs.size()) {
        return createRun();
    }
    if ((pos >= 0) && (pos < runs.size())) {
        XWPFRun run = runs.get(pos);
        CTR ctr = run.getCTR();
        XmlCursor newCursor = ctr.newCursor();
        if (!isCursorInRunContent(newCursor)) {
            // look up correct position for CTP -> XXX -> R array
            newCursor.toParent();
        }
        if (isCursorInRunContent(newCursor)) {
            boolean nextSibling = newCursor.toNextSibling();
            if (!nextSibling) {
                return createRun();
            }
            // provide a new run
            String uri = CTR.type.getName().getNamespaceURI();
            String localPart = "r";
            // creates a new run, cursor is positioned inside the new
            // element
            newCursor.beginElement(localPart, uri);
            // move the cursor to the START token to the run just created
            newCursor.toParent();
            CTR r = ((CTR) (newCursor.getObject()));
            XWPFRun newRun = new XWPFRun(r, ((IRunBody) (this)));
            // To update the iruns, find where we're going
            // in the normal runs, and go in there
            int iPos = sdtElements.size();
            int oldAt = sdtElements.indexOf(run);
            if (oldAt != (-1)) {
                iPos = oldAt;
            }
            if ((iPos + 1) >= sdtElements.size()) {
                sdtElements.add(newRun);
            } else {
                sdtElements.add(iPos, newRun);
            }
            // Runs itself is easy to update
            if ((pos + 1) >= runs.size()) {
                runs.add(newRun);
            } else {
                runs.add(pos + 1, newRun);
            }
            newCursor.dispose();
            return newRun;
        }
        newCursor.dispose();
    }
    return null;
}

public XWPFRun insertNewRunAfter(int pos) {
    if (pos == paragraph.getRuns().size()) {
        return paragraph.createRun();
    }
    if ((pos >= 0) && (pos < paragraph.getRuns().size())) {
        XWPFRun run = paragraph.getRuns().get(pos);
        CTR ctr = run.getCTR();
        XmlCursor newCursor = ctr.newCursor();
        if (!isCursorInParagraph(newCursor)) {
            // look up correct position for CTP -> XXX -> R array
            newCursor.toParent();
        }
        if (isCursorInParagraph(newCursor)) {
            boolean nextSibling = newCursor.toNextSibling();
            if (!nextSibling) {
                return paragraph.createRun();
            }
            String uri = CTR.type.getName().getNamespaceURI();
            String localPart = "r";
            newCursor.beginElement(localPart, uri);
            newCursor.toParent();
            CTR r = ((CTR) (newCursor.getObject()));
            XWPFRun newRun = new XWPFRun(r, ((IRunBody) (this.paragraph)));
            List<IRunElement> iruns = getIRuns();
            List<XWPFRun> runs = getRuns();
            int iPos = iruns.size();
            if (pos < runs.size()) {
                XWPFRun oldAtPos = runs.get(pos);
                int oldAt = iruns.indexOf(oldAtPos);
                if (oldAt != (-1)) {
                    iPos = oldAt;
                }
            }
            if ((iPos + 1) >= iruns.size()) {
                iruns.add(newRun);
            } else {
                iruns.add(iPos + 1, newRun);
            }
            // Runs itself is easy to update
            if ((pos + 1) >= runs.size()) {
                runs.add(newRun);
            } else {
                runs.add(pos + 1, newRun);
            }
            newCursor.dispose();
            return newRun;
        }
        newCursor.dispose();
    }
    return null;
}

@Override
public XWPFTable insertNewTbl(XmlCursor cursor) {
    if (isCursorInBody(cursor)) {
        String uri = CTTbl.type.getName().getNamespaceURI();
        String localPart = "tbl";
        cursor.beginElement(localPart, uri);
        cursor.toParent();
        CTTbl t = ((CTTbl) (cursor.getObject()));
        XWPFTable newT = new XWPFTable(t, this);
        XmlObject o = null;
        while ((!(o instanceof CTTbl)) && cursor.toPrevSibling()) {
            o = cursor.getObject();
        } 
        if (!(o instanceof CTTbl)) {
            tables.add(0, newT);
        } else {
            int pos = tables.indexOf(getTable(((CTTbl) (o)))) + 1;
            tables.add(pos, newT);
        }
        int i = 0;
        XmlCursor tableCursor = t.newCursor();
        try {
            cursor.toCursor(tableCursor);
            while (cursor.toPrevSibling()) {
                o = cursor.getObject();
                if (((o instanceof CTP) || (o instanceof CTTbl)) || (o instanceof CTSdtBlock)) {
                    i++;
                }
            } 
            bodyElements.add(i, newT);
            cursor.toCursor(tableCursor);
            cursor.toEndToken();
            return newT;
        } finally {
            tableCursor.dispose();
        }
    }
    return null;
}

public XWPFRun insertNewRun(int pos) {
    if (pos == runs.size()) {
        return createRun();
    }
    return insertNewProvidedRun(pos, newCursor -> {
        String uri = CTR.type.getName().getNamespaceURI();
        String localPart = "r";
        // creates a new run, cursor is positioned inside the new
        // element
        newCursor.beginElement(localPart, uri);
        // move the cursor to the START token to the run just created
        newCursor.toParent();
        CTR r = ((CTR) (newCursor.getObject()));
        return new XWPFRun(r, ((IRunBody) (this)));
    });
}

