public static void renderComment(XWPFRun run, CommentRenderData data) throws Exception {
    XWPFParagraph paragraph = ((XWPFParagraph) (run.getParent()));
    XWPFParagraphWrapper parentContext = new XWPFParagraphWrapper(paragraph);
    BigInteger cId = BigInteger.ZERO;
    CommentContent commentContent = data.getCommentContent();
    if (null != commentContent) {
        XWPFComments comments = ((NiceXWPFDocument) (paragraph.getDocument())).createComments();
        XWPFComment newComment = comments.createComment(NextIDUtils.getCommentMaxId(comments).add(BigInteger.ONE));
        newComment.setAuthor(commentContent.getAuthor());
        newComment.setDate(commentContent.getDate());
        newComment.setInitials(commentContent.getInitials());
        renderDocument(newComment.createParagraph().createRun(), commentContent.getContent());
        cId = newComment.getCtComment().getId();
        parentContext.insertNewCommentRangeStart(run, cId);
    }
    renderParagraph(run, Paragraphs.of().addList(data.getContents()).create());
    if (null != commentContent) {
        parentContext.insertNewCommentRangeEnd(run, cId);
        XWPFRun newRun = parentContext.insertNewRun(ParagraphUtils.getRunPos(run));
        newRun.getCTR().addNewCommentReference().setId(cId);
    }
}

public static BigInteger getCommentMaxId(XWPFComments docComments) {
    List<XWPFComment> comments = docComments.getComments();
    BigInteger max = BigInteger.ZERO;
    if (null == comments)
        return max;

    for (XWPFComment comment : comments) {
        BigInteger id = comment.getCtComment().getId();
        if ((null != id) && (id.compareTo(max) == 1)) {
            max = id;
        }
    }
    return max;
}

public static void renderPicture(XWPFRun run, PictureRenderData picture) throws Exception {
    byte[] imageBytes = picture.readPictureData();
    if (null == imageBytes) {
        throw new IllegalStateException("Can't read picture byte arrays!");
    }
    PictureType pictureType = picture.getPictureType();
    if (null == pictureType) {
        pictureType = PictureType.suggestFileType(imageBytes);
    }
    if (null == pictureType) {
        throw new RenderException("PictureRenderData must set picture type!");
    }
    PictureStyle style = picture.getPictureStyle();
    if (null == style)
        style = new PictureStyle();

    int width = style.getWidth();
    int height = style.getHeight();
    int svgScale = style.getSvgScale();
    if (pictureType == PictureType.SVG) {
        imageBytes = SVGConvertor.toPng(imageBytes, ((float) (width)), ((float) (height)), svgScale);
        pictureType = PictureType.PNG;
    }
    if (!isSetSize(style)) {
        BufferedImage original = BufferedImageUtils.readBufferedImage(imageBytes);
        width = original.getWidth();
        height = original.getHeight();
        if (style.getScalePattern() == WidthScalePattern.FIT) {
            BodyContainer bodyContainer = BodyContainerFactory.getBodyContainer(run);
            int pageWidth = UnitUtils.twips2Pixel(bodyContainer.elementPageWidth(((IBodyElement) (run.getParent()))));
            if (width > pageWidth) {
                double ratio = pageWidth / ((double) (width));
                width = pageWidth;
                height = ((int) (height * ratio));
            }
        }
    }
    try (InputStream stream = new ByteArrayInputStream(imageBytes)) {
        PictureAlign align = style.getAlign();
        if ((null != align) && (run.getParent() instanceof XWPFParagraph)) {
            ((XWPFParagraph) (run.getParent())).setAlignment(ParagraphAlignment.valueOf(align.ordinal() + 1));
        }
        run.addPicture(stream, pictureType.type(), "Generated", Units.pixelToEMU(width), Units.pixelToEMU(height));
    }
}

