@Override
public void render(ElementTemplate eleTemplate, Object data, XWPFTemplate template) {
    T model = null;
    try {
        model = cast(data);
    } catch (Exception e) {
        throw new RenderException("Error Render Data format for template: " + eleTemplate.getSource(), e);
    }
    RenderContext<T> context = new RenderContext<T>(eleTemplate, model, template);
    try {
        // validate
        if (!validate(model)) {
            postValidError(context);
            return;
        }
        // do render
        beforeRender(context);
        doRender(context);
        afterRender(context);
    } catch (Exception e) {
        reThrowException(context, e);
    }
}

public void resolveParagraph(XWPFParagraph paragraph, List<MetaTemplate> metaTemplates, Deque<BlockTemplate> stack) {
    XWPFParagraphWrapper paragraphWrapper = new XWPFParagraphWrapper(paragraph);
    new RunningRunBody(new ParagraphContext(paragraphWrapper), templatePattern).refactorRun();
    resolveXWPFRuns(paragraph.getRuns(), metaTemplates, stack);
    paragraphWrapper.getSDTs().forEach(sdtEle -> addNewMeta(metaTemplates, stack, resolveSDTElements(sdtEle.getContent())));
}

