        CTOnOff ctWC = (pr.isSetWidowControl()) ? pr.getWidowControl() : pr.addNewWidowControl();
        ctWC.setVal(style.getWidowControl() ? XWPFOnOff.ON : XWPFOnOff.OFF);
    }
    if (null != style.getAllowWordBreak()) {
        // paragraph.setWordWrapped(style.getWordWrap());
        CTOnOff ctWW = (pr.isSetWordWrap()) ? pr.getWordWrap() : pr.addNewWordWrap();
        ctWW.setVal(style.getAllowWordBreak() ? XWPFOnOff.OFF : XWPFOnOff.ON);
    }
    if (null != style.getNumId()) {
        paragraph.setNumID(BigInteger.valueOf(style.getNumId()));
    }
    if (null != style.getLvl()) {
        paragraph.setNumILvl(BigInteger.valueOf(style.getLvl()));
    }
}

/**
 * set row style
 *
 * @param row
 * @param rowStyle
 */
public static void styleTableRow(XWPFTableRow row, RowStyle rowStyle) {
    if ((null == row) || (null == rowStyle))
        return;

    int height = rowStyle.getHeight();
    CTRow ctRow = row.getCtRow();
    CTTrPr properties = (ctRow.isSetTrPr()) ? ctRow.getTrPr() : ctRow.addNewTrPr();
    if (0 != height) {
        row.setHeight(height);
        CTHeight h = (properties.sizeOfTrHeightArray() == 0) ? properties.addNewTrHeight() : properties.getTrHeightArray(0);
        String heightRule = rowStyle.getHeightRule();
        if ("exact".equals(heightRule))
            h.setHRule(STHeightRule.EXACT);
        else if ("atleast".equals(heightRule))
            h.setHRule(STHeightRule.AT_LEAST);
        else
            h.setHRule(STHeightRule.AUTO);

    }
    boolean repeated = rowStyle.isRepeated();
    if (repeated) {
        CTOnOff tblHeader = (properties.sizeOfTblHeaderArray() == 0) ? properties.addNewTblHeader() : properties.getTblHeaderArray(0);
        tblHeader.setVal(XWPFOnOff.ON);
    }
    boolean breakAcrossPage = rowStyle.isBreakAcrossPage();
    if (!breakAcrossPage) {
        if (properties.sizeOfCantSplitArray() == 0) {
            properties.addNewCantSplit();
        } else {
            properties.getCantSplitArray(0).setVal(XWPFOnOff.ON);
        }
    }
}

public BigInteger addNewMultiLevelNumberingId(NumberingFormat... numFmts) {
    XWPFNumbering numbering = this.getNumbering();
    if (null == numbering) {
        numbering = this.createNumbering();
    }
    XWPFNumberingWrapper numberingWrapper = new XWPFNumberingWrapper(numbering);
    CTAbstractNum cTAbstractNum = CTAbstractNum.Factory.newInstance();
    // if we have an existing document, we must determine the next
    // free number first.
    cTAbstractNum.setAbstractNumId(numberingWrapper.getNextAbstractNumID());
    // CTMultiLevelType addNewMultiLevelType = cTAbstractNum.addNewMultiLevelType();
    // addNewMultiLevelType.setVal(STMultiLevelType.HYBRID_MULTILEVEL);
    for (int i = 0; i < numFmts.length; i++) {
        NumberingFormat numFmt = numFmts[i];
        CTLvl cTLvl = cTAbstractNum.addNewLvl();
        CTPPrBase ppr = (cTLvl.isSetPPr()) ? cTLvl.getPPr() : cTLvl.addNewPPr();
        CTInd ind = (ppr.isSetInd()) ? ppr.getInd() : ppr.addNewInd();
        ind.setLeft(BigInteger.valueOf(UnitUtils.cm2Twips(0.74F) * i));
        Enum fmt = STNumberFormat.Enum.forInt(numFmt.getNumFmt());
        String val = numFmt.getLvlText();
        cTLvl.addNewNumFmt().setVal(fmt);
        cTLvl.addNewLvlText().setVal(val);
        cTLvl.addNewStart().setVal(BigInteger.valueOf(1));
        cTLvl.setIlvl(BigInteger.valueOf(i));
        if (fmt == STNumberFormat.BULLET) {
            cTLvl.addNewLvlJc().setVal(STJc.LEFT);
            CTRPr addNewRPr = cTLvl.addNewRPr();
            CTFonts ctFonts = addNewRPr.addNewRFonts();
            ctFonts.setAscii("Wingdings");
            ctFonts.setHAnsi("Wingdings");
            ctFonts.setHint(STHint.DEFAULT);
        }
    }
    XWPFAbstractNum abstractNum = new XWPFAbstractNum(cTAbstractNum);
    BigInteger abstractNumID = numbering.addAbstractNum(abstractNum);
    return numbering.addNum(abstractNumID);
}

