    } else if (xwpfRun instanceof XWPFFieldRun) {
        return new XWPFFieldRun(((CTSimpleField) (((XWPFFieldRun) (xwpfRun)).getCTField().copy())), ((CTR) (((XWPFFieldRun) (xwpfRun)).getCTR().copy())), p);
