public static void renderComment(XWPFRun run, CommentRenderData data) throws Exception {
    XWPFParagraph paragraph = ((XWPFParagraph) (run.getParent()));
    XWPFParagraphWrapper parentContext = new XWPFParagraphWrapper(paragraph);
    BigInteger cId = BigInteger.ZERO;
    CommentContent commentContent = data.getCommentContent();
    if (null != commentContent) {
        XWPFComments comments = ((NiceXWPFDocument) (paragraph.getDocument())).createComments();
        XWPFComment newComment = comments.createComment(NextIDUtils.getCommentMaxId(comments).add(BigInteger.ONE));
        newComment.setAuthor(commentContent.getAuthor());
        newComment.setDate(commentContent.getDate());
        newComment.setInitials(commentContent.getInitials());
        renderDocument(newComment.createParagraph().createRun(), commentContent.getContent());
        cId = newComment.getCtComment().getId();
        parentContext.insertNewCommentRangeStart(run, cId);
    }
    renderParagraph(run, Paragraphs.of().addList(data.getContents()).create());
    if (null != commentContent) {
        parentContext.insertNewCommentRangeEnd(run, cId);
        XWPFRun newRun = parentContext.insertNewRun(ParagraphUtils.getRunPos(run));
        newRun.getCTR().addNewCommentReference().setId(cId);
    }
}

public XWPFRun insertNewRunAfter(int pos) {
    if (pos == runs.size()) {
        return createRun();
    }
    if ((pos >= 0) && (pos < runs.size())) {
        XWPFRun run = runs.get(pos);
        CTR ctr = run.getCTR();
        XmlCursor newCursor = ctr.newCursor();
        if (!isCursorInRunContent(newCursor)) {
            // look up correct position for CTP -> XXX -> R array
            newCursor.toParent();
        }
        if (isCursorInRunContent(newCursor)) {
            boolean nextSibling = newCursor.toNextSibling();
            if (!nextSibling) {
                return createRun();
            }
            // provide a new run
            String uri = CTR.type.getName().getNamespaceURI();
            String localPart = "r";
            // creates a new run, cursor is positioned inside the new
            // element
            newCursor.beginElement(localPart, uri);
            // move the cursor to the START token to the run just created
            newCursor.toParent();
            CTR r = ((CTR) (newCursor.getObject()));
            XWPFRun newRun = new XWPFRun(r, ((IRunBody) (this)));
            // To update the iruns, find where we're going
            // in the normal runs, and go in there
            int iPos = sdtElements.size();
            int oldAt = sdtElements.indexOf(run);
            if (oldAt != (-1)) {
                iPos = oldAt;
            }
            if ((iPos + 1) >= sdtElements.size()) {
                sdtElements.add(newRun);
            } else {
                sdtElements.add(iPos, newRun);
            }
            // Runs itself is easy to update
            if ((pos + 1) >= runs.size()) {
                runs.add(newRun);
            } else {
                runs.add(pos + 1, newRun);
            }
            newCursor.dispose();
            return newRun;
        }
        newCursor.dispose();
    }
    return null;
}

@Override
public void next(IterableTemplate iterable, ParentContext parentContext, IterableContext context, Object model) {
    RunBodyContext paragraphContext = ((RunBodyContext) (parentContext));
    RunTemplate end = iterable.getEndMark();
    CTR endCtr = end.getRun().getCTR();
    int startPos = context.getStart();
    int endPos = context.getEnd();
    // copy position cursor
    int insertPostionCursor = end.getRunPos();
    // copy content
    List<XWPFRun> runs = paragraphContext.getRuns();
    List<XWPFRun> copies = new ArrayList<XWPFRun>();
    for (int i = startPos + 1; i < endPos; i++) {
        insertPostionCursor = end.getRunPos();
        XWPFRun xwpfRun = runs.get(i);
        XWPFRun insertNewRun = paragraphContext.insertNewRun(xwpfRun, insertPostionCursor);
        XWPFRun replaceXwpfRun = paragraphContext.createRun(xwpfRun, paragraphContext.getTarget());
        paragraphContext.setAndUpdateRun(replaceXwpfRun, insertNewRun, insertPostionCursor);
        XmlCursor newCursor = endCtr.newCursor();
        newCursor.toPrevSibling();
        XmlObject object = newCursor.getObject();
        XWPFRun copy = paragraphContext.createRun(object, paragraphContext.getTarget());
        DrawingSupport.updateDocPrId(copy);
        copies.add(copy);
        paragraphContext.setAndUpdateRun(copy, replaceXwpfRun, insertPostionCursor);
    }
    // re-parse
    List<MetaTemplate> templates = this.resolver.resolveXWPFRuns(copies);
    // render
    process(templates, model);
}

@Override
public void doRender(RenderContext<String> context) throws Exception {
    RunTemplate runTemplate = ((RunTemplate) (context.getEleTemplate()));
    XWPFRun run = context.getRun();
    run.setText("", 0);
    XWPFParagraph paragraph = ((XWPFParagraph) (run.getParent()));
    XWPFParagraphWrapper paragraphWrapper = new XWPFParagraphWrapper(paragraph);
    XWPFRun startRun = paragraphWrapper.insertNewRun(runTemplate.getRunPos());
    StyleUtils.styleRun(startRun, run);
    CTR ctr = startRun.getCTR();
    CTFldChar addNewFldChar = ctr.addNewFldChar();
    addNewFldChar.setFldCharType(STFldCharType.BEGIN);
    XWPFRun instrRun = paragraphWrapper.insertNewRun(runTemplate.getRunPos());
    StyleUtils.styleRun(instrRun, run);
    ctr = instrRun.getCTR();
    CTText addNewInstrText = ctr.addNewInstrText();
    // "EQ \\o\\ac(, 1)"
    addNewInstrText.setStringValue(context.getData());
    XWPFRun sepRun = paragraphWrapper.insertNewRun(runTemplate.getRunPos());
    StyleUtils.styleRun(sepRun, run);
    ctr = sepRun.getCTR();
    addNewFldChar = ctr.addNewFldChar();
    addNewFldChar.setFldCharType(STFldCharType.SEPARATE);
    XWPFRun endRun = paragraphWrapper.insertNewRun(runTemplate.getRunPos());
    StyleUtils.styleRun(endRun, run);
    ctr = endRun.getCTR();
    addNewFldChar = ctr.addNewFldChar();
    addNewFldChar.setFldCharType(STFldCharType.END);
}

/**
 * removes a Run at the position pos in the paragraph
 *
 * @return true if the run was removed
 */
public boolean removeRun(int pos) {
    if ((pos >= 0) && (pos < runs.size())) {
        XWPFRun run = runs.get(pos);
        XmlCursor c = run.getCTR().newCursor();
        c.removeXml();
        c.dispose();
        runs.remove(pos);
        sdtElements.remove(run);
        return true;
    }
    return false;
}

