/**
 * set run style by style
 *
 * @param run
 * @param style
 */
public static void styleRun(XWPFRun run, Style style) {
    if ((null == run) || (null == style))
        return;

    CTRPr pr = getRunProperties(run);
    String color = style.getColor();
    if (StringUtils.isNotBlank(color)) {
        // run.setColor(color);
        // issue 326
        CTColor ctColor = (pr.sizeOfColorArray() > 0) ? pr.getColorArray(0) : pr.addNewColor();
        ctColor.setVal(color);
        if (ctColor.isSetThemeColor())
            ctColor.unsetThemeColor();

    }
    double fontSize = style.getFontSize();
    if ((0 != fontSize) && ((-1) != fontSize)) {
        run.setFontSize(fontSize);
    }
    String fontFamily = style.getFontFamily();
    if (StringUtils.isNotBlank(fontFamily)) {
        run.setFontFamily(fontFamily, FontCharRange.eastAsia);
        run.setFontFamily(fontFamily, FontCharRange.ascii);
        run.setFontFamily(fontFamily, FontCharRange.hAnsi);
        run.setFontFamily(fontFamily, FontCharRange.cs);
    }
    String westernFontFamily = style.getWesternFontFamily();
    if (StringUtils.isNotBlank(westernFontFamily)) {
        run.setFontFamily(westernFontFamily, FontCharRange.ascii);
        run.setFontFamily(westernFontFamily, FontCharRange.hAnsi);
        run.setFontFamily(westernFontFamily, FontCharRange.cs);
    }
    XWPFHighlightColor highlightColor = style.getHighlightColor();
    if (null != highlightColor) {
        CTHighlight highlight = (pr.sizeOfHighlightArray() > 0) ? pr.getHighlightArray(0) : pr.addNewHighlight();
        highlight.setVal(STHighlightColor.Enum.forInt(highlightColor.getValue()));
    }
    Boolean bold = style.isBold();
    if (null != bold)
        run.setBold(bold);

    Boolean italic = style.isItalic();
    if (null != italic)
        run.setItalic(italic);

    Boolean strike = style.isStrike();
    if (null != strike)
        run.setStrikeThrough(strike);

    UnderlinePatterns underlinePatern = style.getUnderlinePatterns();
    if (null != underlinePatern) {
        run.setUnderline(underlinePatern);
        if (null != style.getUnderlineColor()) {
            run.setUnderlineColor(style.getUnderlineColor());
        }
    }
    int point = style.getCharacterSpacing();
    // in twentieths of a point
    if ((0 != point) && ((-1) != point))
        run.setCharacterSpacing(UnitUtils.point2Twips(point));

    String vertAlign = style.getVertAlign();
    if (StringUtils.isNotBlank(vertAlign)) {
        run.setVerticalAlignment(vertAlign);
    }
}

@Override
public List<MetaTemplate> resolveBodyElements(List<IBodyElement> bodyElements) {
    List<MetaTemplate> metaTemplates = new ArrayList<>();
    if (null == bodyElements)
        return metaTemplates;

    // current iterable templates state
    Deque<BlockTemplate> stack = new LinkedList<BlockTemplate>();
    for (IBodyElement element : bodyElements) {
        if (element == null)
            continue;

        if (element.getElementType() == BodyElementType.PARAGRAPH) {
            resolveParagraph(((XWPFParagraph) (element)), metaTemplates, stack);
        } else if (element.getElementType() == BodyElementType.TABLE) {
            XWPFTable table = ((XWPFTable) (element));
            List<XWPFTableRow> rows = table.getRows();
            if (null == rows)
                continue;

            for (XWPFTableRow row : rows) {
                resolveTableRow(row, metaTemplates, stack);
            }
        } else if ((element.getElementType() == BodyElementType.CONTENTCONTROL) && (element instanceof XWPFStructuredDocumentTag)) {
            XWPFStructuredDocumentTag sdt = ((XWPFStructuredDocumentTag) (element));
            addNewMeta(metaTemplates, stack, resolveSDTElements(sdt.getContent()));
        }
    }
    checkStack(stack);
    return metaTemplates;
}

public static void updateDocPrId(XWPFRun run) {
    NiceXWPFDocument document = ((NiceXWPFDocument) (run.getDocument()));
    if (!document.getDocPrIdenifierManager().isValid())
        return;

    CTR r = run.getCTR();
    for (CTDrawing ctDrawing : r.getDrawingList()) {
        processCTDrawing(document, ctDrawing);
    }
    XmlObject[] xmlObjects = r.selectPath(IdenifierManagerWrapper.XPATH_DRAWING);
    if ((null == xmlObjects) || (xmlObjects.length <= 0))
        return;

    for (XmlObject xmlObject : xmlObjects) {
        try {
            CTDrawing ctDrawing = CTDrawing.Factory.parse(xmlObject.xmlText());
            processCTDrawing(document, ctDrawing);
            xmlObject.set(ctDrawing);
        } catch (XmlException e) {
            // no-op
        }
    }
}

public XWPFRun insertNewRunAfter(int pos) {
    if (pos == paragraph.getRuns().size()) {
        return paragraph.createRun();
    }
    if ((pos >= 0) && (pos < paragraph.getRuns().size())) {
        XWPFRun run = paragraph.getRuns().get(pos);
        CTR ctr = run.getCTR();
        XmlCursor newCursor = ctr.newCursor();
        if (!isCursorInParagraph(newCursor)) {
            // look up correct position for CTP -> XXX -> R array
            newCursor.toParent();
        }
        if (isCursorInParagraph(newCursor)) {
            boolean nextSibling = newCursor.toNextSibling();
            if (!nextSibling) {
                return paragraph.createRun();
            }
            String uri = CTR.type.getName().getNamespaceURI();
            String localPart = "r";
            newCursor.beginElement(localPart, uri);
            newCursor.toParent();
            CTR r = ((CTR) (newCursor.getObject()));
            XWPFRun newRun = new XWPFRun(r, ((IRunBody) (this.paragraph)));
            List<IRunElement> iruns = getIRuns();
            List<XWPFRun> runs = getRuns();
            int iPos = iruns.size();
            if (pos < runs.size()) {
                XWPFRun oldAtPos = runs.get(pos);
                int oldAt = iruns.indexOf(oldAtPos);
                if (oldAt != (-1)) {
                    iPos = oldAt;
                }
            }
            if ((iPos + 1) >= iruns.size()) {
                iruns.add(newRun);
            } else {
                iruns.add(iPos + 1, newRun);
            }
            // Runs itself is easy to update
            if ((pos + 1) >= runs.size()) {
                runs.add(newRun);
            } else {
                runs.add(pos + 1, newRun);
            }
            newCursor.dispose();
            return newRun;
        }
        newCursor.dispose();
    }
    return null;
}

public XWPFRun insertNewRun(int pos) {
    if (pos == runs.size()) {
        return createRun();
    }
    return insertNewProvidedRun(pos, newCursor -> {
        String uri = CTR.type.getName().getNamespaceURI();
        String localPart = "r";
        // creates a new run, cursor is positioned inside the new
        // element
        newCursor.beginElement(localPart, uri);
        // move the cursor to the START token to the run just created
        newCursor.toParent();
        CTR r = ((CTR) (newCursor.getObject()));
        return new XWPFRun(r, ((IRunBody) (this)));
    });
}

public void resolveParagraph(XWPFParagraph paragraph, List<MetaTemplate> metaTemplates, Deque<BlockTemplate> stack) {
    XWPFParagraphWrapper paragraphWrapper = new XWPFParagraphWrapper(paragraph);
    new RunningRunBody(new ParagraphContext(paragraphWrapper), templatePattern).refactorRun();
    resolveXWPFRuns(paragraph.getRuns(), metaTemplates, stack);
    paragraphWrapper.getSDTs().forEach(sdtEle -> addNewMeta(metaTemplates, stack, resolveSDTElements(sdtEle.getContent())));
}

@Override
public void doRender(ChartTemplate eleTemplate, ChartMultiSeriesRenderData data, XWPFTemplate template) throws Exception {
    XWPFChart chart = eleTemplate.getChart();
    List<XDDFChartData> chartSeries = chart.getChartSeries();
    validate(chartSeries, data);
    int totalSeriesCount = ensureSeriesCount(chart, chartSeries);
    int valueCol = 1;
    List<SeriesRenderData> usedSeriesDatas = new ArrayList<>();
    for (XDDFChartData chartData : chartSeries) {
        int orignSize = chartData.getSeriesCount();
        List<SeriesRenderData> currentSeriesData = null;
        if (chartSeries.size() <= 1) {
            // ignore combo type
            currentSeriesData = data.getSeriesDatas();
        } else {
            currentSeriesData = obtainSeriesData(chartData.getClass(), data.getSeriesDatas());
        }
        usedSeriesDatas.addAll(currentSeriesData);
        int currentSeriesSize = currentSeriesData.size();
        XDDFDataSource<?> categoriesData = null;
        if (chartData instanceof XDDFScatterChartData) {
            categoriesData = createNumbericalDataSource(chart, toNumberArray(data.getCategories()), 0);
        } else {
            categoriesData = createStringDataSource(chart, data.getCategories(), 0);
        }
        for (int i = 0; i < currentSeriesSize; i++) {
            XDDFNumericalDataSource<? extends Number> valuesData = createNumbericalDataSource(chart, currentSeriesData.get(i).getValues(), valueCol);
            XDDFChartData.Series currentSeries = null;
            if (i < orignSize) {
                currentSeries = chartData.getSeries(i);
                valuesData.setFormatCode(currentSeries.getValuesData().getFormatCode());
                currentSeries.replaceData(categoriesData, valuesData);
            } else {
                // add series, should copy series with style
                currentSeries = chartData.addSeries(categoriesData, valuesData);
                processNewSeries(chartData, currentSeries);
            }
            String name = currentSeriesData.get(i).getName();
            currentSeries.setTitle(name, chart.setSheetTitle(name, valueCol));
            valueCol++;
        }
        // clear extra series
        removeExtraSeries(chartData, orignSize, currentSeriesSize);
    }
    XSSFSheet sheet = chart.getWorkbook().getSheetAt(0);
    updateCTTable(sheet, usedSeriesDatas);
    removeExtraSheetCell(sheet, data.getCategories().length, totalSeriesCount, usedSeriesDatas.size());
    for (XDDFChartData chartData : chartSeries) {
        plot(chart, chartData);
    }
    setTitle(chart, data.getChartTitle());
    setAxisTitle(chart, data.getxAxisTitle(), data.getyAxisTitle());
}

