public static void renderPicture(XWPFRun run, PictureRenderData picture) throws Exception {
    byte[] imageBytes = picture.readPictureData();
    if (null == imageBytes) {
        throw new IllegalStateException("Can't read picture byte arrays!");
    }
    PictureType pictureType = picture.getPictureType();
    if (null == pictureType) {
        pictureType = PictureType.suggestFileType(imageBytes);
    }
    if (null == pictureType) {
        throw new RenderException("PictureRenderData must set picture type!");
    }
    PictureStyle style = picture.getPictureStyle();
    if (null == style)
        style = new PictureStyle();

    int width = style.getWidth();
    int height = style.getHeight();
    int svgScale = style.getSvgScale();
    if (pictureType == PictureType.SVG) {
        imageBytes = SVGConvertor.toPng(imageBytes, ((float) (width)), ((float) (height)), svgScale);
        pictureType = PictureType.PNG;
    }
    if (!isSetSize(style)) {
        BufferedImage original = BufferedImageUtils.readBufferedImage(imageBytes);
        width = original.getWidth();
        height = original.getHeight();
        if (style.getScalePattern() == WidthScalePattern.FIT) {
            BodyContainer bodyContainer = BodyContainerFactory.getBodyContainer(run);
            int pageWidth = UnitUtils.twips2Pixel(bodyContainer.elementPageWidth(((IBodyElement) (run.getParent()))));
            if (width > pageWidth) {
                double ratio = pageWidth / ((double) (width));
                width = pageWidth;
                height = ((int) (height * ratio));
            }
        }
    }
    try (InputStream stream = new ByteArrayInputStream(imageBytes)) {
        PictureAlign align = style.getAlign();
        if ((null != align) && (run.getParent() instanceof XWPFParagraph)) {
            ((XWPFParagraph) (run.getParent())).setAlignment(ParagraphAlignment.valueOf(align.ordinal() + 1));
        }
        run.addPicture(stream, pictureType.type(), "Generated", Units.pixelToEMU(width), Units.pixelToEMU(height));
    }
}

public static void stylePpr(XWPFParagraph paragraph, ParagraphStyle style) {
    if ((null == paragraph) || (null == style))
        return;

    if (null != style.getAlign()) {
        paragraph.setAlignment(style.getAlign());
    }
    if (null != style.getSpacing()) {
        paragraph.setSpacingBetween(style.getSpacing(), null == style.getSpacingRule() ? LineSpacingRule.AUTO : style.getSpacingRule());
    }
    if (null != style.getSpacingBeforeLines()) {
        paragraph.setSpacingBeforeLines(new BigInteger(String.valueOf(Math.round(style.getSpacingBeforeLines() * 100.0))).intValue());
    }
    if (null != style.getSpacingAfterLines()) {
        paragraph.setSpacingAfterLines(new BigInteger(String.valueOf(Math.round(style.getSpacingAfterLines() * 100.0))).intValue());
    }
    if (null != style.getSpacingBefore()) {
        paragraph.setSpacingBefore(UnitUtils.point2Twips(style.getSpacingBefore()));
    }
    if (null != style.getSpacingAfter()) {
        paragraph.setSpacingAfter(UnitUtils.point2Twips(style.getSpacingAfter()));
    }
    CTP ctp = paragraph.getCTP();
    CTPPr pr = (ctp.isSetPPr()) ? ctp.getPPr() : ctp.addNewPPr();
    CTInd indent = (pr.isSetInd()) ? pr.getInd() : pr.addNewInd();
    if (null != style.getIndentLeftChars()) {
        BigInteger bi = new BigInteger(String.valueOf(Math.round(style.getIndentLeftChars() * 100.0)));
        indent.setLeftChars(bi);
        if (indent.isSetLeft())
            indent.unsetLeft();

    }
    if (null != style.getIndentRightChars()) {
        BigInteger bi = new BigInteger(String.valueOf(Math.round(style.getIndentRightChars() * 100.0)));
        indent.setRightChars(bi);
        if (indent.isSetRight())
            indent.unsetRight();

    }
    if (null != style.getIndentHangingChars()) {
        BigInteger bi = new BigInteger(String.valueOf(Math.round(style.getIndentHangingChars() * 100.0)));
        indent.setHangingChars(bi);
        if (indent.isSetHanging())
            indent.unsetHanging();

    }
    if (null != style.getIndentFirstLineChars()) {
        BigInteger bi = new BigInteger(String.valueOf(Math.round(style.getIndentFirstLineChars() * 100.0)));
        indent.setFirstLineChars(bi);
        if (indent.isSetFirstLine())
            indent.unsetFirstLine();

    }
    CTPBdr ct = (pr.isSetPBdr()) ? pr.getPBdr() : pr.addNewPBdr();
    if (null != style.getLeftBorder()) {
        styleCTBorder(ct.isSetLeft() ? ct.getLeft() : ct.addNewLeft(), style.getLeftBorder());
    }
    if (null != style.getTopBorder()) {
        styleCTBorder(ct.isSetTop() ? ct.getTop() : ct.addNewTop(), style.getTopBorder());
    }
    if (null != style.getRightBorder()) {
        styleCTBorder(ct.isSetRight() ? ct.getRight() : ct.addNewRight(), style.getRightBorder());
    }
    if (null != style.getBottomBorder()) {
        styleCTBorder(ct.isSetBottom() ? ct.getBottom() : ct.addNewBottom(), style.getBottomBorder());
    }
    if (null != style.getBackgroundColor()) {
        CTShd shd = (pr.isSetShd()) ? pr.getShd() : pr.addNewShd();
        XWPFShadingPattern shadingPattern = style.getShadingPattern();
        if (null == shadingPattern) {
            shd.setVal(STShd.CLEAR);
        } else {
            shd.setVal(STShd.Enum.forInt(shadingPattern.getValue()));
        }
        shd.setColor("auto");
        shd.setFill(style.getBackgroundColor());
    }
    if (null != style.getStyleId()) {
        paragraph.setStyle(style.getStyleId());
    }
    if (null != style.getKeepLines()) {
        CTOnOff ctKeepLines = (pr.isSetKeepLines()) ? pr.getKeepLines() : pr.addNewKeepLines();
        ctKeepLines.setVal(style.getKeepLines() ? XWPFOnOff.ON : XWPFOnOff.OFF);
    }
    if (null != style.getKeepNext()) {
        paragraph.setKeepNext(style.getKeepNext());
    }
    if (null != style.getPageBreakBefore()) {
        paragraph.setPageBreak(style.getPageBreakBefore());
    }
    if (null != style.getWidowControl()) {
        CTOnOff ctWC = (pr.isSetWidowControl()) ? pr.getWidowControl() : pr.addNewWidowControl();
        ctWC.setVal(style.getWidowControl() ? XWPFOnOff.ON : XWPFOnOff.OFF);
    }
    if (null != style.getAllowWordBreak()) {
        // paragraph.setWordWrapped(style.getWordWrap());
        CTOnOff ctWW = (pr.isSetWordWrap()) ? pr.getWordWrap() : pr.addNewWordWrap();
        ctWW.setVal(style.getAllowWordBreak() ? XWPFOnOff.OFF : XWPFOnOff.ON);
    }
    if (null != style.getNumId()) {
        paragraph.setNumID(BigInteger.valueOf(style.getNumId()));
    }
    if (null != style.getLvl()) {
        paragraph.setNumILvl(BigInteger.valueOf(style.getLvl()));
    }
}

public static void renderComment(XWPFRun run, CommentRenderData data) throws Exception {
    XWPFParagraph paragraph = ((XWPFParagraph) (run.getParent()));
    XWPFParagraphWrapper parentContext = new XWPFParagraphWrapper(paragraph);
    BigInteger cId = BigInteger.ZERO;
    CommentContent commentContent = data.getCommentContent();
    if (null != commentContent) {
        XWPFComments comments = ((NiceXWPFDocument) (paragraph.getDocument())).createComments();
        XWPFComment newComment = comments.createComment(NextIDUtils.getCommentMaxId(comments).add(BigInteger.ONE));
        newComment.setAuthor(commentContent.getAuthor());
        newComment.setDate(commentContent.getDate());
        newComment.setInitials(commentContent.getInitials());
        renderDocument(newComment.createParagraph().createRun(), commentContent.getContent());
        cId = newComment.getCtComment().getId();
        parentContext.insertNewCommentRangeStart(run, cId);
    }
    renderParagraph(run, Paragraphs.of().addList(data.getContents()).create());
    if (null != commentContent) {
        parentContext.insertNewCommentRangeEnd(run, cId);
        XWPFRun newRun = parentContext.insertNewRun(ParagraphUtils.getRunPos(run));
        newRun.getCTR().addNewCommentReference().setId(cId);
    }
}

public static BigInteger getCommentMaxId(XWPFComments docComments) {
    List<XWPFComment> comments = docComments.getComments();
    BigInteger max = BigInteger.ZERO;
    if (null == comments)
        return max;

    for (XWPFComment comment : comments) {
        BigInteger id = comment.getCtComment().getId();
        if ((null != id) && (id.compareTo(max) == 1)) {
            max = id;
        }
    }
    return max;
}

@Override
public XWPFParagraph insertNewParagraph(XmlCursor cursor) {
    if (isCursorInBody(cursor)) {
        String uri = CTP.type.getName().getNamespaceURI();
        /* TODO DO not use a coded constant, find the constant in the OOXML
        classes instead, as the child of type CT_Paragraph is defined in the
        OOXML schema as 'p'
         */
        String localPart = "p";
        // creates a new Paragraph, cursor is positioned inside the new
        // element
        cursor.beginElement(localPart, uri);
        // move the cursor to the START token to the paragraph just created
        cursor.toParent();
        CTP p = ((CTP) (cursor.getObject()));
        XWPFParagraph newP = new XWPFParagraph(p, this);
        XmlObject o = null;
        /* move the cursor to the previous element until a) the next
        paragraph is found or b) all elements have been passed
         */
        while ((!(o instanceof CTP)) && cursor.toPrevSibling()) {
            o = cursor.getObject();
        } 
        /* if the object that has been found is a) not a paragraph or b) is
        the paragraph that has just been inserted, as the cursor in the
        while loop above was not moved as there were no other siblings,
        then the paragraph that was just inserted is the first paragraph
        in the body. Otherwise, take the previous paragraph and calculate
        the new index for the new paragraph.
         */
        if ((!(o instanceof CTP)) || (o == p)) {
            paragraphs.add(0, newP);
        } else {
            int pos = paragraphs.indexOf(getParagraph(((CTP) (o)))) + 1;
            paragraphs.add(pos, newP);
        }
        /* create a new cursor, that points to the START token of the just
        inserted paragraph
         */
        XmlCursor newParaPos = p.newCursor();
        try {
            /* Calculate the paragraphs index in the list of all body
            elements
             */
            int i = 0;
            cursor.toCursor(newParaPos);
            while (cursor.toPrevSibling()) {
                o = cursor.getObject();
                if (((o instanceof CTP) || (o instanceof CTTbl)) || (o instanceof CTSdtBlock)) {
                    i++;
                }
            } 
            bodyElements.add(i, newP);
            cursor.toCursor(newParaPos);
            cursor.toEndToken();
            return newP;
        } finally {
            newParaPos.dispose();
        }
    }
    return null;
}

/**
 * add a new paragraph at position of the cursor
 *
 * @param cursor
 * 		The XmlCursor structure created with XmlBeans
 * @return the inserted paragraph
 */
@Override
public XWPFParagraph insertNewParagraph(final XmlCursor cursor) {
    if (!isCursorInTextBox(cursor)) {
        return null;
    }
    String uri = CTP.type.getName().getNamespaceURI();
    String localPart = "p";
    cursor.beginElement(localPart, uri);
    cursor.toParent();
    CTP p = ((CTP) (cursor.getObject()));
    XWPFParagraph newP = new XWPFParagraph(p, this);
    XmlObject o = null;
    while ((!(o instanceof CTP)) && cursor.toPrevSibling()) {
        o = cursor.getObject();
    } 
    if ((!(o instanceof CTP)) || (o == p)) {
        paragraphs.add(0, newP);
    } else {
        int pos = paragraphs.indexOf(getParagraph(((CTP) (o)))) + 1;
        paragraphs.add(pos, newP);
    }
    int i = 0;
    XmlCursor p2 = p.newCursor();
    cursor.toCursor(p2);
    p2.dispose();
    while (cursor.toPrevSibling()) {
        o = cursor.getObject();
        if ((o instanceof CTP) || (o instanceof CTTbl)) {
            i++;
        }
    } 
    bodyElements.add(i, newP);
    p2 = p.newCursor();
    cursor.toCursor(p2);
    p2.dispose();
    cursor.toEndToken();
    return newP;
}

@Override
public void render(ElementTemplate eleTemplate, Object data, XWPFTemplate template) {
    RunTemplate runTemplate = ((RunTemplate) (eleTemplate));
    XWPFRun run = runTemplate.getRun();
    try {
        if (!TableTools.isInsideTable(run)) {
            throw new IllegalStateException((("The template tag " + runTemplate.getSource()) + " must be inside a ") + "table");
        }
        XWPFTableCell tagCell = ((XWPFTableCell) (((XWPFParagraph) (run.getParent())).getBody()));
        XWPFTableRow tableRow = tagCell.getTableRow();
        XWPFTable table = tableRow.getTable();
        run.setText("", 0);
        int loopColumnNum = getLoopColumnNum(tagCell);
        int templateColIndex = getTemplateColIndex(tagCell);
        int rowSize = table.getRows().size();
        int index = 0;
        if (data instanceof Iterable) {
            int dataSize = getSize(((Iterable<?>) (data)));
            int[] loopColWidths = processLoopColWidth(table, tableRow, templateColIndex, dataSize, loopColumnNum);
            int[] newCellInsertPoint4row = new int[rowSize];
            XWPFTableCell[] cursorCell4row = new XWPFTableCell[rowSize];
            boolean initFlag = true;
            TemplateResolver resolver = new TemplateResolver(template.getConfig().copy(prefix, suffix));
            Iterator<?> iterator = ((Iterable<?>) (data)).iterator();
            boolean hasNext = iterator.hasNext();
            while (hasNext) {
                Object root = iterator.next();
                hasNext = iterator.hasNext();
                List<XWPFTableCell> cells = new ArrayList<>();
                int loopCellStartPoint = (index * loopColumnNum) + templateColIndex;
                for (int j = 0; j < (loopColumnNum * 2); j += 2) {
                    for (int i = 0; i < rowSize; i++) {
                        XWPFTableRow row = table.getRow(i);
                        int actualLoopCellPosition = getActualInsertPosition(row, loopCellStartPoint + j);
                        if ((-1) == actualLoopCellPosition) {
                            addColGridSpan(row, loopCellStartPoint + j);
                            continue;
                        }
                        XWPFTableCell loopCell = row.getCell(actualLoopCellPosition);
                        if (initFlag) {
                            loopCell.setWidth(String.valueOf(loopColWidths[j / 2]));
                            if (j == 0) {
                                cursorCell4row[i] = loopCell;
                                newCellInsertPoint4row[i] = actualLoopCellPosition;
                            }
                        }
                        insertCell(row, newCellInsertPoint4row[i]);
                        setTableCell(row, loopCell, newCellInsertPoint4row[i]);
                        // double set row
                        XmlCursor newCursor = cursorCell4row[i].getCTTc().newCursor();
                        newCursor.toPrevSibling();
                        XmlObject object = newCursor.getObject();
                        XWPFTableCell nextCell = new XWPFTableCell(((CTTc) (object)), row, ((IBody) (loopCell.getPart())));
                        setTableCell(row, nextCell, newCellInsertPoint4row[i]++);
                        cells.add(nextCell);
                    }
                }
                initFlag = false;
                RenderDataCompute dataCompute = template.getConfig().getRenderDataComputeFactory().newCompute(EnvModel.of(root, EnvIterator.makeEnv(index++, hasNext)));
                cells.forEach(cell -> {
                    List<MetaTemplate> templates = resolver.resolveBodyElements(cell.getBodyElements());
                    new DocumentProcessor(template, resolver, dataCompute).process(templates);
                });
            } 
        }
        int endPoint = ((index * loopColumnNum) + templateColIndex) - 1;
        for (int j = loopColumnNum; j > 0; j--) {
            for (int i = 0; i < rowSize; i++) {
                XWPFTableRow row = table.getRow(i);
                int actualInsertPosition = getActualInsertPosition(row, endPoint + j);
                if ((-1) == actualInsertPosition) {
                    minusGridSpan(row, endPoint + j);
                    continue;
                }
                removeCell(row, actualInsertPosition);
            }
        }
        afterloop(table, data);
    } catch (Exception e) {
        throw new RenderException("failed to render table multi-column template", e);
    }
}

@Override
public void next(IterableTemplate iterable, ParentContext parentContext, IterableContext context, Object model) {
    BodyContainer bodyContainer = ((BodyContainer) (parentContext));
    XWPFParagraph endParagraph = ((XWPFParagraph) (iterable.getEndRun().getParent()));
    CTP endCtp = endParagraph.getCTP();
    int start = context.getStart();
    int end = context.getEnd();
    context.getNumberingContinue().resetCache();
    // copy positon cursor
    XmlCursor insertPostionCursor = endCtp.newCursor();
    // copy content
    List<IBodyElement> bodyElements = bodyContainer.getBodyElements();
    List<IBodyElement> copies = new ArrayList<IBodyElement>();
    for (int i = start + 1; i < end; i++) {
        IBodyElement iBodyElement = bodyElements.get(i);
        if (iBodyElement.getElementType() == BodyElementType.PARAGRAPH) {
            insertPostionCursor = endCtp.newCursor();
            XWPFParagraph insertNewParagraph = bodyContainer.insertNewParagraph(insertPostionCursor);
            // find insert paragraph pos
            int paraPos = bodyContainer.getParaPos(insertNewParagraph);
            bodyContainer.setParagraph(((XWPFParagraph) (iBodyElement)), paraPos);
            // re-update ctp reference
            insertPostionCursor = endCtp.newCursor();
            insertPostionCursor.toPrevSibling();
            XmlObject object = insertPostionCursor.getObject();
            XWPFParagraph copy = new XWPFParagraph(((CTP) (object)), bodyContainer.getTarget());
            // update docpr
            DrawingSupport.updateDocPrId(copy);
            // update numbering
            context.getNumberingContinue().updateNumbering(((XWPFParagraph) (iBodyElement)), copy);
            copies.add(copy);
            bodyContainer.updateBodyElements(insertNewParagraph, copy);
            bodyContainer.setParagraph(copy, paraPos);
        } else if (iBodyElement.getElementType() == BodyElementType.TABLE) {
            insertPostionCursor = endCtp.newCursor();
            XWPFTable insertNewTbl = bodyContainer.insertNewTbl(insertPostionCursor);
            // find insert table pos
            int tablePos = bodyContainer.getTablePos(insertNewTbl);
            bodyContainer.setTable(tablePos, ((XWPFTable) (iBodyElement)));
            insertPostionCursor = endCtp.newCursor();
            insertPostionCursor.toPrevSibling();
            XmlObject object = insertPostionCursor.getObject();
            XWPFTable copy = new XWPFTable(((CTTbl) (object)), bodyContainer.getTarget());
            DrawingSupport.updateDocPrId(copy);
            copies.add(copy);
            bodyContainer.updateBodyElements(insertNewTbl, copy);
            bodyContainer.setTable(tablePos, copy);
        }
    }
    // re-parse
    List<MetaTemplate> templates = this.resolver.resolveBodyElements(copies);
    // render
    process(templates, model);
}

