public void updateNumbering(XWPFParagraph source, XWPFParagraph target) {
    XWPFDocument document = source.getDocument();
    XWPFNumbering numbering = document.getNumbering();
    if (null == numbering)
        return;

    BigInteger numID = source.getNumID();
    if (numID == null)
        return;

    if ((null != continueNumID) && numID.equals(continueNumID)) {
        return;
    }
    if (consistCache.get(numID) != null) {
        target.setNumID(consistCache.get(numID));
        return;
    }
    XWPFNumberingWrapper wrapper = new XWPFNumberingWrapper(numbering);
    XWPFNum num = numbering.getNum(numID);
    if (null == num)
        return;

    XWPFAbstractNum abstractNum = numbering.getAbstractNum(num.getCTNum().getAbstractNumId().getVal());
    CTAbstractNum ctAbstractNum = ((CTAbstractNum) (abstractNum.getAbstractNum().copy()));
    ctAbstractNum.setAbstractNumId(wrapper.getNextAbstractNumID());
    // clear continues list
    // (related to tracking numbering definitions when documents are
    // repurposed and
    // changed
    if (ctAbstractNum.isSetNsid())
        ctAbstractNum.unsetNsid();

    // related to where the definition can be displayed in the user
    // interface
    if (ctAbstractNum.isSetTmpl())
        ctAbstractNum.unsetTmpl();

    BigInteger abstractNumID = numbering.addAbstractNum(new XWPFAbstractNum(ctAbstractNum));
    BigInteger newNumId = numbering.addNum(abstractNumID);
    target.setNumID(newNumId);
    consistCache.put(numID, newNumId);
}

@Override
public NiceXWPFDocument merge(NiceXWPFDocument source, Iterator<NiceXWPFDocument> mergeIterator, XWPFRun run) throws Exception {
    CTBody body = source.getDocument().getBody();
    List<String> addParts = createMergeableStrings(source, mergeIterator);
    String[] startEnd = truncatedStartEndXmlFragment(body);
    XWPFParagraph paragraph = ((XWPFParagraph) (run.getParent()));
    CTP mergedContainer = paragraph.getCTP();
    CTP mergedBody = CTP.Factory.parse((((startEnd[0] + "<w:POITL>") + String.join("", addParts)) + "</w:POITL>") + startEnd[1]);
    // instead insert xml-fragment?
    mergedContainer.set(mergedBody);
    String xmlText = truncatedOverlapWP(body);
    body.set(CTBody.Factory.parse(xmlText));
    return source.generate(true);
}

public static void renderCell(XWPFTableCell cell, CellRenderData data, CellStyle defaultCellStyle, Style defaultTextStyle) throws Exception {
    if (null == data)
        return;

    StyleUtils.styleTableCell(cell, defaultCellStyle);
    StyleUtils.styleTableCell(cell, data.getCellStyle());
    List<ParagraphStyle> defaultParaStyles = new ArrayList<>();
    if (null != defaultTextStyle) {
        defaultParaStyles.add(ParagraphStyle.builder().withDefaultTextStyle(defaultTextStyle).build());
    }
    if (null != defaultCellStyle) {
        defaultParaStyles.add(defaultCellStyle.getDefaultParagraphStyle());
    }
    if (null != data.getCellStyle()) {
        defaultParaStyles.add(data.getCellStyle().getDefaultParagraphStyle());
    }
    List<ParagraphRenderData> contents = data.getParagraphs();
    if ((null != contents) && (!contents.isEmpty())) {
        BodyContainer bodyContainer = BodyContainerFactory.getBodyContainer(cell);
        XWPFParagraph placeHolder = cell.getParagraphArray(0);
        if (null == placeHolder)
            placeHolder = cell.addParagraph();

        for (ParagraphRenderData item : contents) {
            XWPFParagraph paragraph = bodyContainer.insertNewParagraph(placeHolder.getCTP().newCursor());
            ParagraphRenderPolicy.Helper.renderParagraph(paragraph.createRun(), item, defaultParaStyles);
        }
        List<XWPFParagraph> paragraphs = cell.getParagraphs();
        int pos = paragraphs.indexOf(placeHolder);
        if ((-1) != pos)
            cell.removeParagraph(pos);

    }
}

