public static void borderTable(XWPFTable table, int size) {
    CTTblPr tblPr = getTblPr(table);
    CTTblBorders tblBorders = tblPr.getTblBorders();
    if (null == tblBorders) {
        tblBorders = tblPr.addNewTblBorders();
    }
    BigInteger borderSize = BigInteger.valueOf(size);
    if (!tblBorders.isSetBottom())
        tblBorders.addNewBottom();

    if (!tblBorders.isSetLeft())
        tblBorders.addNewLeft();

    if (!tblBorders.isSetTop())
        tblBorders.addNewTop();

    if (!tblBorders.isSetRight())
        tblBorders.addNewRight();

    if (!tblBorders.isSetInsideH())
        tblBorders.addNewInsideH();

    if (!tblBorders.isSetInsideV())
        tblBorders.addNewInsideV();

    tblBorders.getBottom().setSz(borderSize);
    tblBorders.getLeft().setSz(borderSize);
    tblBorders.getTop().setSz(borderSize);
    tblBorders.getRight().setSz(borderSize);
    tblBorders.getInsideH().setSz(borderSize);
    tblBorders.getInsideV().setSz(borderSize);
}

@Override
public List<MetaTemplate> resolveBodyElements(List<IBodyElement> bodyElements) {
    List<MetaTemplate> metaTemplates = new ArrayList<>();
    if (null == bodyElements)
        return metaTemplates;

    // current iterable templates state
    Deque<BlockTemplate> stack = new LinkedList<BlockTemplate>();
    for (IBodyElement element : bodyElements) {
        if (element == null)
            continue;

        if (element.getElementType() == BodyElementType.PARAGRAPH) {
            resolveParagraph(((XWPFParagraph) (element)), metaTemplates, stack);
        } else if (element.getElementType() == BodyElementType.TABLE) {
            XWPFTable table = ((XWPFTable) (element));
            List<XWPFTableRow> rows = table.getRows();
            if (null == rows)
                continue;

            for (XWPFTableRow row : rows) {
                resolveTableRow(row, metaTemplates, stack);
            }
        } else if ((element.getElementType() == BodyElementType.CONTENTCONTROL) && (element instanceof XWPFStructuredDocumentTag)) {
            XWPFStructuredDocumentTag sdt = ((XWPFStructuredDocumentTag) (element));
            addNewMeta(metaTemplates, stack, resolveSDTElements(sdt.getContent()));
        }
    }
    checkStack(stack);
    return metaTemplates;
}

@Override
public List<MetaTemplate> resolveXWPFRuns(List<XWPFRun> runs) {
    List<MetaTemplate> metaTemplates = new ArrayList<>();
    if (runs == null)
        return metaTemplates;

    Deque<BlockTemplate> stack = new LinkedList<BlockTemplate>();
    resolveXWPFRuns(runs, metaTemplates, stack);
    checkStack(stack);
    return metaTemplates;
}

public static void invoke(RenderPolicy policy, ElementTemplate eleTemplate, Object data, XWPFTemplate template) {
    Objects.requireNonNull(policy, ("Cannot find render policy: [" + eleTemplate.getTagName()) + "]");
    Object model = data;
    List<PreRenderDataCastor> preRenderDataCastors = template.getConfig().getPreRenderDataCastors();
    if (null != preRenderDataCastors) {
        for (PreRenderDataCastor preRenderDataCastor : preRenderDataCastors) {
            model = preRenderDataCastor.preCast(policy, model);
        }
    }
    LOGGER.info("Start render Template {}, Sign:{}, policy:{}", eleTemplate, logChar(eleTemplate.getSign()), ClassUtils.getShortClassName(policy.getClass()));
    policy.render(eleTemplate, model, template);
}

public void resolveParagraph(XWPFParagraph paragraph, List<MetaTemplate> metaTemplates, Deque<BlockTemplate> stack) {
    XWPFParagraphWrapper paragraphWrapper = new XWPFParagraphWrapper(paragraph);
    new RunningRunBody(new ParagraphContext(paragraphWrapper), templatePattern).refactorRun();
    resolveXWPFRuns(paragraph.getRuns(), metaTemplates, stack);
    paragraphWrapper.getSDTs().forEach(sdtEle -> addNewMeta(metaTemplates, stack, resolveSDTElements(sdtEle.getContent())));
}

public ReflectionException(String name, Class<?> clazz, Exception e) {
    this((("Error Reflect " + name) + "from class ") + ClassUtils.getShortClassName(clazz), e);
}

public static Object getValue(String fieldName, Object obj) {
    Objects.requireNonNull(obj, "Class must not be null");
    Objects.requireNonNull(fieldName, "Name must not be null");
    Field field = findField(obj.getClass(), fieldName);
    if (null == field) {
        throw new ReflectionException((("No Such field " + fieldName) + " from class") + ClassUtils.getShortClassName(obj.getClass()));
    }
    try {
        field.setAccessible(true);
        return field.get(obj);
    } catch (Exception e) {
        throw new ReflectionException(fieldName, obj.getClass(), e);
    }
}

public BigInteger addNewMultiLevelNumberingId(NumberingFormat... numFmts) {
    XWPFNumbering numbering = this.getNumbering();
    if (null == numbering) {
        numbering = this.createNumbering();
    }
    XWPFNumberingWrapper numberingWrapper = new XWPFNumberingWrapper(numbering);
    CTAbstractNum cTAbstractNum = CTAbstractNum.Factory.newInstance();
    // if we have an existing document, we must determine the next
    // free number first.
    cTAbstractNum.setAbstractNumId(numberingWrapper.getNextAbstractNumID());
    // CTMultiLevelType addNewMultiLevelType = cTAbstractNum.addNewMultiLevelType();
    // addNewMultiLevelType.setVal(STMultiLevelType.HYBRID_MULTILEVEL);
    for (int i = 0; i < numFmts.length; i++) {
        NumberingFormat numFmt = numFmts[i];
        CTLvl cTLvl = cTAbstractNum.addNewLvl();
        CTPPrBase ppr = (cTLvl.isSetPPr()) ? cTLvl.getPPr() : cTLvl.addNewPPr();
        CTInd ind = (ppr.isSetInd()) ? ppr.getInd() : ppr.addNewInd();
        ind.setLeft(BigInteger.valueOf(UnitUtils.cm2Twips(0.74F) * i));
        Enum fmt = STNumberFormat.Enum.forInt(numFmt.getNumFmt());
        String val = numFmt.getLvlText();
        cTLvl.addNewNumFmt().setVal(fmt);
        cTLvl.addNewLvlText().setVal(val);
        cTLvl.addNewStart().setVal(BigInteger.valueOf(1));
        cTLvl.setIlvl(BigInteger.valueOf(i));
        if (fmt == STNumberFormat.BULLET) {
            cTLvl.addNewLvlJc().setVal(STJc.LEFT);
            CTRPr addNewRPr = cTLvl.addNewRPr();
            CTFonts ctFonts = addNewRPr.addNewRFonts();
            ctFonts.setAscii("Wingdings");
            ctFonts.setHAnsi("Wingdings");
            ctFonts.setHint(STHint.DEFAULT);
        }
    }
    XWPFAbstractNum abstractNum = new XWPFAbstractNum(cTAbstractNum);
    BigInteger abstractNumID = numbering.addAbstractNum(abstractNum);
    return numbering.addNum(abstractNumID);
}

