/**
 * set run style by other run
 *
 * @param dest
 * @param src
 */
public static void styleRun(XWPFRun dest, XWPFRun src) {
    if ((null == dest) || (null == src))
        return;

    if (StringUtils.isNotEmpty(src.getStyle()))
        dest.setStyle(src.getStyle());

    if (Boolean.TRUE.equals(src.isBold()))
        dest.setBold(src.isBold());

    if (StringUtils.isNotBlank(src.getColor()))
        dest.setColor(src.getColor());

    if (0 != src.getCharacterSpacing())
        dest.setCharacterSpacing(src.getCharacterSpacing());

    if (StringUtils.isNotBlank(src.getFontFamily(FontCharRange.ascii)))
        dest.setFontFamily(src.getFontFamily(), FontCharRange.ascii);

    if (StringUtils.isNotBlank(src.getFontFamily(FontCharRange.eastAsia)))
        dest.setFontFamily(src.getFontFamily(), FontCharRange.eastAsia);

    if (StringUtils.isNotBlank(src.getFontFamily(FontCharRange.hAnsi)))
        dest.setFontFamily(src.getFontFamily(), FontCharRange.hAnsi);

    if (StringUtils.isNotBlank(src.getFontFamily(FontCharRange.cs)))
        dest.setFontFamily(src.getFontFamily(), FontCharRange.cs);

    Double fontSize = src.getFontSizeAsDouble();
    if (null != fontSize) {
        dest.setFontSize(fontSize);
    }
    if (Boolean.TRUE.equals(src.isItalic()))
        dest.setItalic(src.isItalic());

    if (Boolean.TRUE.equals(src.isStrikeThrough()))
        dest.setStrikeThrough(src.isStrikeThrough());

    if (UnderlinePatterns.NONE != src.getUnderline())
        dest.setUnderline(src.getUnderline());

    if (null != src.getUnderlineColor())
        dest.setUnderlineColor(src.getUnderlineColor());

}

public static Style retriveStyle(XWPFRun run) {
    if (null == run)
        return null;

    StyleBuilder builder = Style.builder().buildColor(run.getColor()).buildFontFamily(run.getFontFamily(FontCharRange.eastAsia)).buildWesternFontFamily(run.getFontFamily(FontCharRange.ascii));
    if (null != run.getFontSizeAsDouble())
        builder.buildFontSize(run.getFontSizeAsDouble());

    if (run.isBold())
        builder.buildBold();

    if (run.isItalic())
        builder.buildItalic();

    if (run.isStrikeThrough())
        builder.buildStrike();

    return builder.build();
}

public static void renderComment(XWPFRun run, CommentRenderData data) throws Exception {
    XWPFParagraph paragraph = ((XWPFParagraph) (run.getParent()));
    XWPFParagraphWrapper parentContext = new XWPFParagraphWrapper(paragraph);
    BigInteger cId = BigInteger.ZERO;
    CommentContent commentContent = data.getCommentContent();
    if (null != commentContent) {
        XWPFComments comments = ((NiceXWPFDocument) (paragraph.getDocument())).createComments();
        XWPFComment newComment = comments.createComment(NextIDUtils.getCommentMaxId(comments).add(BigInteger.ONE));
        newComment.setAuthor(commentContent.getAuthor());
        newComment.setDate(commentContent.getDate());
        newComment.setInitials(commentContent.getInitials());
        renderDocument(newComment.createParagraph().createRun(), commentContent.getContent());
        cId = newComment.getCtComment().getId();
        parentContext.insertNewCommentRangeStart(run, cId);
    }
    renderParagraph(run, Paragraphs.of().addList(data.getContents()).create());
    if (null != commentContent) {
        parentContext.insertNewCommentRangeEnd(run, cId);
        XWPFRun newRun = parentContext.insertNewRun(ParagraphUtils.getRunPos(run));
        newRun.getCTR().addNewCommentReference().setId(cId);
    }
}

@Override
public NiceXWPFDocument merge(NiceXWPFDocument source, Iterator<NiceXWPFDocument> mergeIterator, XWPFRun run) throws Exception {
    CTBody body = source.getDocument().getBody();
    List<String> addParts = createMergeableStrings(source, mergeIterator);
    String[] startEnd = truncatedStartEndXmlFragment(body);
    XWPFParagraph paragraph = ((XWPFParagraph) (run.getParent()));
    CTP mergedContainer = paragraph.getCTP();
    CTP mergedBody = CTP.Factory.parse((((startEnd[0] + "<w:POITL>") + String.join("", addParts)) + "</w:POITL>") + startEnd[1]);
    // instead insert xml-fragment?
    mergedContainer.set(mergedBody);
    String xmlText = truncatedOverlapWP(body);
    body.set(CTBody.Factory.parse(xmlText));
    return source.generate(true);
}

