                if (!ctp.isSetPPr())
                    continue;

                CTPPr pPr = ctp.getPPr();
                if (pPr.isSetSectPr()) {
                    XWPFSection xwpfSection = new XWPFSection(pPr.getSectPr());
                    if (headerFooter instanceof XWPFHeader) {
                        if (xwpfSection.haveHeader(relationId))
                            return xwpfSection;

                    } else if (headerFooter instanceof XWPFFooter) {
                        if (xwpfSection.haveFooter(relationId))
                            return xwpfSection;

                    }
                }
            }
        }
    }
    CTBody body = doc.getDocument().getBody();
    if (body.isSetSectPr()) {
        return new XWPFSection(body.getSectPr());
    }
    return null;
}

