public BigInteger addNewMultiLevelNumberingId(NumberingFormat... numFmts) {
    XWPFNumbering numbering = this.getNumbering();
    if (null == numbering) {
        numbering = this.createNumbering();
    }
    XWPFNumberingWrapper numberingWrapper = new XWPFNumberingWrapper(numbering);
    CTAbstractNum cTAbstractNum = CTAbstractNum.Factory.newInstance();
    // if we have an existing document, we must determine the next
    // free number first.
    cTAbstractNum.setAbstractNumId(numberingWrapper.getNextAbstractNumID());
    // CTMultiLevelType addNewMultiLevelType = cTAbstractNum.addNewMultiLevelType();
    // addNewMultiLevelType.setVal(STMultiLevelType.HYBRID_MULTILEVEL);
    for (int i = 0; i < numFmts.length; i++) {
        NumberingFormat numFmt = numFmts[i];
        CTLvl cTLvl = cTAbstractNum.addNewLvl();
        CTPPrBase ppr = (cTLvl.isSetPPr()) ? cTLvl.getPPr() : cTLvl.addNewPPr();
        CTInd ind = (ppr.isSetInd()) ? ppr.getInd() : ppr.addNewInd();
        ind.setLeft(BigInteger.valueOf(UnitUtils.cm2Twips(0.74F) * i));
        Enum fmt = STNumberFormat.Enum.forInt(numFmt.getNumFmt());
        String val = numFmt.getLvlText();
        cTLvl.addNewNumFmt().setVal(fmt);
        cTLvl.addNewLvlText().setVal(val);
        cTLvl.addNewStart().setVal(BigInteger.valueOf(1));
        cTLvl.setIlvl(BigInteger.valueOf(i));
        if (fmt == STNumberFormat.BULLET) {
            cTLvl.addNewLvlJc().setVal(STJc.LEFT);
            CTRPr addNewRPr = cTLvl.addNewRPr();
            CTFonts ctFonts = addNewRPr.addNewRFonts();
            ctFonts.setAscii("Wingdings");
            ctFonts.setHAnsi("Wingdings");
            ctFonts.setHint(STHint.DEFAULT);
        }
    }
    XWPFAbstractNum abstractNum = new XWPFAbstractNum(cTAbstractNum);
    BigInteger abstractNumID = numbering.addAbstractNum(abstractNum);
    return numbering.addNum(abstractNumID);
}

@Override
public void render(ElementTemplate eleTemplate, Object data, XWPFTemplate template) {
    RunTemplate runTemplate = ((RunTemplate) (eleTemplate));
    XWPFRun run = runTemplate.getRun();
    try {
        if (!TableTools.isInsideTable(run)) {
            throw new IllegalStateException(("The template tag " + runTemplate.getSource()) + " must be inside a table");
        }
        XWPFTableCell tagCell = ((XWPFTableCell) (((XWPFParagraph) (run.getParent())).getBody()));
        XWPFTable table = tagCell.getTableRow().getTable();
        run.setText("", 0);
        int templateColIndex = getColIndex(tagCell);
        int rowSize = table.getRows().size();
        for (int i = rowSize - 1; i >= 0; i--) {
            XWPFTableRow row = table.getRow(i);
            int actualInsertPosition = getActualInsertPosition(row, templateColIndex);
            if ((-1) == actualInsertPosition) {
                minusGridSpan(row, templateColIndex);
                continue;
            }
            String text = row.getCell(actualInsertPosition).getText();
            if ("_delete_".equals(text)) {
                table.removeRow(i);
            } else {
                removeCell(row, actualInsertPosition);
            }
        }
    } catch (Exception e) {
    }
}

@Override
public void render(ElementTemplate eleTemplate, Object data, XWPFTemplate template) {
    RunTemplate runTemplate = ((RunTemplate) (eleTemplate));
    XWPFRun run = runTemplate.getRun();
    try {
        if (!TableTools.isInsideTable(run)) {
            throw new IllegalStateException((("The template tag " + runTemplate.getSource()) + " must be inside a ") + "table");
        }
        XWPFTableCell tagCell = ((XWPFTableCell) (((XWPFParagraph) (run.getParent())).getBody()));
        XWPFTableRow tableRow = tagCell.getTableRow();
        XWPFTable table = tableRow.getTable();
        run.setText("", 0);
        int loopColumnNum = getLoopColumnNum(tagCell);
        int templateColIndex = getTemplateColIndex(tagCell);
        int rowSize = table.getRows().size();
        int index = 0;
        if (data instanceof Iterable) {
            int dataSize = getSize(((Iterable<?>) (data)));
            int[] loopColWidths = processLoopColWidth(table, tableRow, templateColIndex, dataSize, loopColumnNum);
            int[] newCellInsertPoint4row = new int[rowSize];
            XWPFTableCell[] cursorCell4row = new XWPFTableCell[rowSize];
            boolean initFlag = true;
            TemplateResolver resolver = new TemplateResolver(template.getConfig().copy(prefix, suffix));
            Iterator<?> iterator = ((Iterable<?>) (data)).iterator();
            boolean hasNext = iterator.hasNext();
            while (hasNext) {
                Object root = iterator.next();
                hasNext = iterator.hasNext();
                List<XWPFTableCell> cells = new ArrayList<>();
                int loopCellStartPoint = (index * loopColumnNum) + templateColIndex;
                for (int j = 0; j < (loopColumnNum * 2); j += 2) {
                    for (int i = 0; i < rowSize; i++) {
                        XWPFTableRow row = table.getRow(i);
                        int actualLoopCellPosition = getActualInsertPosition(row, loopCellStartPoint + j);
                        if ((-1) == actualLoopCellPosition) {
                            addColGridSpan(row, loopCellStartPoint + j);
                            continue;
                        }
                        XWPFTableCell loopCell = row.getCell(actualLoopCellPosition);
                        if (initFlag) {
                            loopCell.setWidth(String.valueOf(loopColWidths[j / 2]));
                            if (j == 0) {
                                cursorCell4row[i] = loopCell;
                                newCellInsertPoint4row[i] = actualLoopCellPosition;
                            }
                        }
                        insertCell(row, newCellInsertPoint4row[i]);
                        setTableCell(row, loopCell, newCellInsertPoint4row[i]);
                        // double set row
                        XmlCursor newCursor = cursorCell4row[i].getCTTc().newCursor();
                        newCursor.toPrevSibling();
                        XmlObject object = newCursor.getObject();
                        XWPFTableCell nextCell = new XWPFTableCell(((CTTc) (object)), row, ((IBody) (loopCell.getPart())));
                        setTableCell(row, nextCell, newCellInsertPoint4row[i]++);
                        cells.add(nextCell);
                    }
                }
                initFlag = false;
                RenderDataCompute dataCompute = template.getConfig().getRenderDataComputeFactory().newCompute(EnvModel.of(root, EnvIterator.makeEnv(index++, hasNext)));
                cells.forEach(cell -> {
                    List<MetaTemplate> templates = resolver.resolveBodyElements(cell.getBodyElements());
                    new DocumentProcessor(template, resolver, dataCompute).process(templates);
                });
            } 
        }
        int endPoint = ((index * loopColumnNum) + templateColIndex) - 1;
        for (int j = loopColumnNum; j > 0; j--) {
            for (int i = 0; i < rowSize; i++) {
                XWPFTableRow row = table.getRow(i);
                int actualInsertPosition = getActualInsertPosition(row, endPoint + j);
                if ((-1) == actualInsertPosition) {
                    minusGridSpan(row, endPoint + j);
                    continue;
                }
                removeCell(row, actualInsertPosition);
            }
        }
        afterloop(table, data);
    } catch (Exception e) {
        throw new RenderException("failed to render table multi-column template", e);
    }
}

/**
 * merge several columns of cells in the same row, but do'not remove extra cells
 *
 * @param table
 * 		table
 * @param row
 * 		index of the row
 * @param fromCol
 * 		from column to be merged
 * @param toCol
 * 		to column to be merged
 */
public static void mergeCellsHorizontalWithoutRemove(XWPFTable table, int row, int fromCol, int toCol) {
    Preconditions.requireGreaterThan(toCol, fromCol, "To column to be merged must greater than from column.");
    XWPFTableCell cell = table.getRow(row).getCell(fromCol);
    CTTcPr tcPr = getTcPr(cell);
    tcPr.addNewGridSpan();
    tcPr.getGridSpan().setVal(BigInteger.valueOf(((long) ((toCol - fromCol) + 1))));
    int tcw = 0;
    for (int colIndex = fromCol; colIndex <= toCol; colIndex++) {
        XWPFTableCell tableCell = table.getRow(row).getCell(colIndex);
        // TODO pct, auto
        if (TableWidthType.DXA == tableCell.getWidthType()) {
            if ((-1) == tableCell.getWidth())
                return;

            tcw += tableCell.getWidth();
        } else {
            return;
        }
    }
    if (0 != tcw)
        cell.setWidth(tcw + "");

}

@Override
public void render(ElementTemplate eleTemplate, Object data, XWPFTemplate template) {
    RunTemplate runTemplate = ((RunTemplate) (eleTemplate));
    XWPFRun run = runTemplate.getRun();
    try {
        if (!TableTools.isInsideTable(run)) {
            throw new IllegalStateException(("The template tag " + runTemplate.getSource()) + " must be inside a table");
        }
        XWPFTableCell tagCell = ((XWPFTableCell) (((XWPFParagraph) (run.getParent())).getBody()));
        XWPFTable table = tagCell.getTableRow().getTable();
        run.setText("", 0);
        int templateColIndex = getTemplateColIndex(tagCell);
        int actualColIndex = getActualInsertPosition(tagCell.getTableRow(), templateColIndex);
        XWPFTableCell firstCell = tagCell.getTableRow().getCell(actualColIndex);
        int width = firstCell.getWidth();
        TableWidthType widthType = firstCell.getWidthType();
        if ((TableWidthType.DXA != widthType) || (width == 0)) {
            throw new IllegalArgumentException("template col must set width in centimeters.");
        }
        int rowSize = table.getRows().size();
        if ((null != data) && (data instanceof Iterable)) {
            int colWidth = processLoopColWidth(table, width, templateColIndex, data);
            Iterator<?> iterator = ((Iterable<?>) (data)).iterator();
            int insertPosition = templateColIndex;
            TemplateResolver resolver = new TemplateResolver(template.getConfig().copy(prefix, suffix));
            int index = 0;
            boolean hasNext = iterator.hasNext();
            while (hasNext) {
                Object root = iterator.next();
                hasNext = iterator.hasNext();
                insertPosition = templateColIndex++;
                List<XWPFTableCell> cells = new ArrayList<XWPFTableCell>();
                for (int i = 0; i < rowSize; i++) {
                    XWPFTableRow row = table.getRow(i);
                    int actualInsertPosition = getActualInsertPosition(row, insertPosition);
                    if ((-1) == actualInsertPosition) {
                        addColGridSpan(row, insertPosition);
                        continue;
                    }
                    XWPFTableCell templateCell = row.getCell(actualInsertPosition);
                    templateCell.setWidth(colWidth + "");
                    XWPFTableCell nextCell = insertCell(row, actualInsertPosition);
                    setTableCell(row, templateCell, actualInsertPosition);
                    // double set row
                    XmlCursor newCursor = templateCell.getCTTc().newCursor();
                    newCursor.toPrevSibling();
                    XmlObject object = newCursor.getObject();
                    nextCell = new XWPFTableCell(((CTTc) (object)), row, ((IBody) (nextCell.getPart())));
                    setTableCell(row, nextCell, actualInsertPosition);
                    cells.add(nextCell);
                }
                RenderDataCompute dataCompute = template.getConfig().getRenderDataComputeFactory().newCompute(EnvModel.of(root, EnvIterator.makeEnv(index++, hasNext)));
                cells.forEach(cell -> {
                    List<MetaTemplate> templates = resolver.resolveBodyElements(cell.getBodyElements());
                    new DocumentProcessor(template, resolver, dataCompute).process(templates);
                });
            } 
        }
        for (int i = 0; i < rowSize; i++) {
            XWPFTableRow row = table.getRow(i);
            int actualInsertPosition = getActualInsertPosition(row, templateColIndex);
            if ((-1) == actualInsertPosition) {
                minusGridSpan(row, templateColIndex);
                continue;
            }
            removeCell(row, actualInsertPosition);
        }
        afterloop(table, data);
    } catch (Exception e) {
        throw new RenderException((("HackLoopTable for " + eleTemplate) + "error: ") + e.getMessage(), e);
    }
}

