@Override
public List<MetaTemplate> resolveXWPFRuns(List<XWPFRun> runs) {
    List<MetaTemplate> metaTemplates = new ArrayList<>();
    if (runs == null)
        return metaTemplates;

    Deque<BlockTemplate> stack = new LinkedList<BlockTemplate>();
    resolveXWPFRuns(runs, metaTemplates, stack);
    checkStack(stack);
    return metaTemplates;
}

public RunningRunBody(RunBodyContext context, Pattern pattern) {
    this.runBodyContext = context;
    this.runs = context.getRuns();
    if ((null == runs) || runs.isEmpty())
        return;

    Matcher matcher = pattern.matcher(getText(context));
    if (matcher.find()) {
        refactorBody();
    }
    buildRunEdge(pattern);
}

public void resolveParagraph(XWPFParagraph paragraph, List<MetaTemplate> metaTemplates, Deque<BlockTemplate> stack) {
    XWPFParagraphWrapper paragraphWrapper = new XWPFParagraphWrapper(paragraph);
    new RunningRunBody(new ParagraphContext(paragraphWrapper), templatePattern).refactorRun();
    resolveXWPFRuns(paragraph.getRuns(), metaTemplates, stack);
    paragraphWrapper.getSDTs().forEach(sdtEle -> addNewMeta(metaTemplates, stack, resolveSDTElements(sdtEle.getContent())));
}

public List<XWPFRun> refactorRun() {
    if (pairs.isEmpty())
        return null;

    List<XWPFRun> templateRuns = new ArrayList<XWPFRun>();
    int size = pairs.size();
    Pair<RunEdge, RunEdge> runEdgePair;
    for (int n = size - 1; n >= 0; n--) {
        runEdgePair = pairs.get(n);
        RunEdge startEdge = runEdgePair.getLeft();
        RunEdge endEdge = runEdgePair.getRight();
        int startRunPos = startEdge.getRunPos();
        int endRunPos = endEdge.getRunPos();
        int startOffset = startEdge.getRunEdge();
        int endOffset = endEdge.getRunEdge();
        String startText = runs.get(startRunPos).text();
        String endText = runs.get(endRunPos).text();
        if ((endOffset + 1) >= endText.length()) {
            // delete the redundant end Run directly
            if (startRunPos != endRunPos)
                runBodyContext.removeRun(endRunPos);

        } else {
            // split end run, set extra in a run
            String extra = endText.substring(endOffset + 1, endText.length());
            if (startRunPos == endRunPos) {
                // create run and set extra content
                XWPFRun extraRun = runBodyContext.insertNewRunAfter(endRunPos);
                StyleUtils.styleRun(extraRun, runs.get(endRunPos));
                buildExtra(extra, extraRun);
            } else {
                // Set the extra content to the redundant end run
                XWPFRun extraRun = runs.get(endRunPos);
                buildExtra(extra, extraRun);
            }
        }
        // remove extra run
        for (int m = endRunPos - 1; m > startRunPos; m--) {
            runBodyContext.removeRun(m);
        }
        if (startOffset <= 0) {
            // set the start Run directly
            XWPFRun templateRun = runs.get(startRunPos);
            templateRun.setText(startEdge.getTag(), 0);
            templateRuns.add(runs.get(startRunPos));
        } else {
            // split start run, set extra in a run
            String extra = startText.substring(0, startOffset);
            XWPFRun extraRun = runs.get(startRunPos);
            buildExtra(extra, extraRun);
            XWPFRun templateRun = runBodyContext.insertNewRunAfter(startRunPos);
            StyleUtils.styleRun(templateRun, extraRun);
            templateRun.setText(startEdge.getTag(), 0);
            templateRuns.add(runs.get(startRunPos + 1));
        }
    }
    return templateRuns;
}

public void updateNumbering(XWPFParagraph source, XWPFParagraph target) {
    XWPFDocument document = source.getDocument();
    XWPFNumbering numbering = document.getNumbering();
    if (null == numbering)
        return;

    BigInteger numID = source.getNumID();
    if (numID == null)
        return;

    if ((null != continueNumID) && numID.equals(continueNumID)) {
        return;
    }
    if (consistCache.get(numID) != null) {
        target.setNumID(consistCache.get(numID));
        return;
    }
    XWPFNumberingWrapper wrapper = new XWPFNumberingWrapper(numbering);
    XWPFNum num = numbering.getNum(numID);
    if (null == num)
        return;

    XWPFAbstractNum abstractNum = numbering.getAbstractNum(num.getCTNum().getAbstractNumId().getVal());
    CTAbstractNum ctAbstractNum = ((CTAbstractNum) (abstractNum.getAbstractNum().copy()));
    ctAbstractNum.setAbstractNumId(wrapper.getNextAbstractNumID());
    // clear continues list
    // (related to tracking numbering definitions when documents are
    // repurposed and
    // changed
    if (ctAbstractNum.isSetNsid())
        ctAbstractNum.unsetNsid();

    // related to where the definition can be displayed in the user
    // interface
    if (ctAbstractNum.isSetTmpl())
        ctAbstractNum.unsetTmpl();

    BigInteger abstractNumID = numbering.addAbstractNum(new XWPFAbstractNum(ctAbstractNum));
    BigInteger newNumId = numbering.addNum(abstractNumID);
    target.setNumID(newNumId);
    consistCache.put(numID, newNumId);
}

public static void renderComment(XWPFRun run, CommentRenderData data) throws Exception {
    XWPFParagraph paragraph = ((XWPFParagraph) (run.getParent()));
    XWPFParagraphWrapper parentContext = new XWPFParagraphWrapper(paragraph);
    BigInteger cId = BigInteger.ZERO;
    CommentContent commentContent = data.getCommentContent();
    if (null != commentContent) {
        XWPFComments comments = ((NiceXWPFDocument) (paragraph.getDocument())).createComments();
        XWPFComment newComment = comments.createComment(NextIDUtils.getCommentMaxId(comments).add(BigInteger.ONE));
        newComment.setAuthor(commentContent.getAuthor());
        newComment.setDate(commentContent.getDate());
        newComment.setInitials(commentContent.getInitials());
        renderDocument(newComment.createParagraph().createRun(), commentContent.getContent());
        cId = newComment.getCtComment().getId();
        parentContext.insertNewCommentRangeStart(run, cId);
    }
    renderParagraph(run, Paragraphs.of().addList(data.getContents()).create());
    if (null != commentContent) {
        parentContext.insertNewCommentRangeEnd(run, cId);
        XWPFRun newRun = parentContext.insertNewRun(ParagraphUtils.getRunPos(run));
        newRun.getCTR().addNewCommentReference().setId(cId);
    }
}

