public static String loadContent(String url) throws IOException {
    ClassLoader cl = getDefaultClassLoader();
    InputStream stream = (null != cl) ? cl.getResourceAsStream(url) : ClassLoader.getSystemResourceAsStream(url);
    try {
        return new String(IOUtils.toByteArray(stream));
    } finally {
        IOUtils.closeQuietly(stream);
    }
}

/**
 * Get picture byte array of BufferedImage
 *
 * @param image
 * @param format
 * 		format of the image
 * @return  */
public static byte[] getBufferByteArray(BufferedImage image, String format) {
    if (null == image)
        return null;

    String formatName = (StringUtils.isNotEmpty(format) && (format.charAt(0) == '.')) ? format.substring(1) : format;
    if (StringUtils.isEmpty(formatName))
        formatName = "png";

    ByteArrayOutputStream os = new ByteArrayOutputStream();
    try {
        ImageIO.write(image, formatName, os);
        return os.toByteArray();
    } catch (Exception e) {
        logger.error("getBufferByteArray error", e);
        return null;
    } finally {
        IOUtils.closeQuietly(os);
    }
}

/**
 * read byte
 *
 * @param image
 * @return  */
public static BufferedImage readBufferedImage(byte[] image) {
    ByteArrayInputStream inputStream = new ByteArrayInputStream(image);
    try {
        return ImageIO.read(inputStream);
    } catch (IOException e) {
        logger.error("readBufferedImage IO error", e);
        throw new RuntimeException(e);
    } finally {
        IOUtils.closeQuietly(inputStream);
    }
}

