/**
 * merge several columns of cells in the same row
 *
 * @param table
 * 		table
 * @param row
 * 		index of the row
 * @param fromCol
 * 		from column to be merged
 * @param toCol
 * 		to column to be merged
 */
public static void mergeCellsHorizonal(XWPFTable table, int row, int fromCol, int toCol) {
    Preconditions.requireGreaterThan(toCol, fromCol, "To column to be merged must greater than from column.");
    mergeCellsHorizontalWithoutRemove(table, row, fromCol, toCol);
    XWPFTableRow rowTable = table.getRow(row);
    for (int colIndex = fromCol + 1; colIndex <= toCol; colIndex++) {
        rowTable.removeCell(fromCol + 1);
        if (rowTable.getTableCells().size() != rowTable.getCtRow().sizeOfTcArray()) {
            rowTable.getCtRow().removeTc(fromCol + 1);
        }
    }
}

public static XWPFTable renderTable(XWPFRun run, TableRenderData data) throws Exception {
    BodyContainer bodyContainer = BodyContainerFactory.getBodyContainer(run);
    XWPFTable table = bodyContainer.insertNewTable(run, data.obtainRowSize(), data.obtainColSize());
    StyleUtils.styleTable(table, data.getTableStyle());
    int size = table.getRows().size();
    for (int i = 0; i < size; i++) {
        RowRenderData rowRenderData = data.getRows().get(i);
        renderRow(table.getRows().get(i), rowRenderData, StyleUtils.retriveStyle(run));
    }
    applyMergeRule(table, data.getMergeRule());
    return table;
}

@Override
public void render(ElementTemplate eleTemplate, Object data, XWPFTemplate template) {
    RunTemplate runTemplate = ((RunTemplate) (eleTemplate));
    XWPFRun run = runTemplate.getRun();
