        XWPFTableCell firstCell = tagCell.getTableRow().getCell(actualColIndex);
        int width = firstCell.getWidth();
        TableWidthType widthType = firstCell.getWidthType();
        if ((TableWidthType.DXA != widthType) || (width == 0)) {
