    return table.getRows().get(0).getTableCells().size();
}

@Override
public void render(ElementTemplate eleTemplate, Object data, XWPFTemplate template) {
    RunTemplate runTemplate = ((RunTemplate) (eleTemplate));
    XWPFRun run = runTemplate.getRun();
    try {
        if (!TableTools.isInsideTable(run)) {
            throw new IllegalStateException(("The template tag " + runTemplate.getSource()) + " must be inside a table");
        }
        XWPFTableCell tagCell = ((XWPFTableCell) (((XWPFParagraph) (run.getParent())).getBody()));
        XWPFTable table = tagCell.getTableRow().getTable();
        run.setText("", 0);
        int templateRowIndex = getTemplateRowIndex(tagCell);
        if ((null != data) && (data instanceof Iterable)) {
            Iterator<?> iterator = ((Iterable<?>) (data)).iterator();
            XWPFTableRow templateRow = table.getRow(templateRowIndex);
            int insertPosition = templateRowIndex;
            TemplateResolver resolver = new TemplateResolver(template.getConfig().copy(prefix, suffix));
            boolean firstFlag = true;
            int index = 0;
            boolean hasNext = iterator.hasNext();
            while (hasNext) {
                Object root = iterator.next();
                hasNext = iterator.hasNext();
                insertPosition = templateRowIndex++;
                XWPFTableRow nextRow = table.insertNewTableRow(insertPosition);
                setTableRow(table, templateRow, insertPosition);
                // double set row
                XmlCursor newCursor = templateRow.getCtRow().newCursor();
                newCursor.toPrevSibling();
                XmlObject object = newCursor.getObject();
                nextRow = new XWPFTableRow(((CTRow) (object)), table);
                if (!firstFlag) {
                    // update VMerge cells for non-first row
                    List<XWPFTableCell> tableCells = nextRow.getTableCells();
                    for (XWPFTableCell cell : tableCells) {
                        CTTcPr tcPr = TableTools.getTcPr(cell);
                        CTVMerge vMerge = tcPr.getVMerge();
                        if (null == vMerge)
                            continue;

                        if (STMerge.RESTART == vMerge.getVal()) {
                            vMerge.setVal(STMerge.CONTINUE);
                        }
                    }
                } else {
                    firstFlag = false;
                }
                setTableRow(table, nextRow, insertPosition);
                RenderDataCompute dataCompute = template.getConfig().getRenderDataComputeFactory().newCompute(EnvModel.of(root, EnvIterator.makeEnv(index++, hasNext)));
                List<XWPFTableCell> cells = nextRow.getTableCells();
                cells.forEach(cell -> {
                    List<MetaTemplate> templates = resolver.resolveBodyElements(cell.getBodyElements());
                    new DocumentProcessor(template, resolver, dataCompute).process(templates);
                });
            } 
        }
        table.removeRow(templateRowIndex);
        afterloop(table, data);
    } catch (Exception e) {
        throw new RenderException((("HackLoopTable for " + eleTemplate) + " error: ") + e.getMessage(), e);
    }
}

@Override
public void render(ElementTemplate eleTemplate, Object data, XWPFTemplate template) {
    try {
        RunTemplate runTemplate = cast2runTemplate(eleTemplate);
        XWPFRun run = runTemplate.getRun();
        checkTargetIsTable(run, ("Processing [" + runTemplate.getTagName()) + "] failed, the target content is not a table");
        XWPFTableCell tagCell = ((XWPFTableCell) (((XWPFParagraph) (run.getParent())).getBody()));
        final XWPFTable table = tagCell.getTableRow().getTable();
        run.setText("", 0);
        TemplateResolver resolver = new TemplateResolver(template.getConfig().copy(prefix, suffix));
        // 
        int position = getRowIndex(tagCell.getTableRow());
        List<XWPFTableRow> tempRows = getAllTemplateRow(table, position);
        if ((null != data) && (data instanceof Iterable)) {
            // 
            final XWPFTableRow firstTempRow = tempRows.get(0);
            Iterator<?> dataIt = ((Iterable<?>) (data)).iterator();
            boolean hasNextData = dataIt.hasNext();
            int index = 0;
            while (hasNextData) {
                Object dt = dataIt.next();
                hasNextData = dataIt.hasNext();
                Iterator<XWPFTableRow> rowTempIt = tempRows.iterator();
                boolean hasNextTempRow = rowTempIt.hasNext();
                while (hasNextTempRow) {
                    XWPFTableRow tempRow = rowTempIt.next();
                    hasNextTempRow = rowTempIt.hasNext();
                    if (!table.addRow(tempRow, position)) {
                        throw new RenderException("");
                    }
                    // 
                    XmlCursor newCursor = firstTempRow.getCtRow().newCursor();
                    newCursor.toPrevSibling();
                    XmlObject object = newCursor.getObject();
                    XWPFTableRow newRow = new XWPFTableRow(((CTRow) (object)), table);
                    newRow.getCtRow().set(object);
                    setTableRow(table, newRow, position);
                    List<XWPFTableCell> cells = newRow.getTableCells();
                    RenderDataCompute dataCompute = template.getConfig().getRenderDataComputeFactory().newCompute(EnvModel.of(dt, EnvIterator.makeEnv(index, hasNextData || hasNextTempRow)));
                    cells.forEach(tableCell -> {
                        List<MetaTemplate> metaTemplates = resolver.resolveBodyElements(tableCell.getBodyElements());
                        new DocumentProcessor(template, resolver, dataCompute).process(metaTemplates);
                    });
                    ++position;
                } 
                ++index;
            } 
        }
        removeTableRow(table, position, tempRows.size());
    } catch (Exception e) {
        throw new RenderException("failed to render table multi-row template", e);
    }
}

