public static void renderParagraph(XWPFRun run, ParagraphRenderData data, List<ParagraphStyle> defaultControlStyles) throws Exception {
    List<RenderData> contents = data.getContents();
    XWPFParagraph paragraph = ((XWPFParagraph) (run.getParent()));
    styleParagraphWithDefaultStyle(paragraph, defaultControlStyles);
    StyleUtils.styleParagraph(paragraph, data.getParagraphStyle());
    XWPFParagraphWrapper parentContext = new XWPFParagraphWrapper(paragraph);
    for (RenderData content : contents) {
        XWPFRun fragment = parentContext.insertNewRun(ParagraphUtils.getRunPos(run));
        StyleUtils.styleRun(fragment, run);
        if (content instanceof TextRenderData) {
            styleRunWithDefaultStyle(fragment, defaultControlStyles);
            StyleUtils.styleRun(fragment, null == data.getParagraphStyle() ? null : data.getParagraphStyle().getDefaultTextStyle());
            TextRenderPolicy.Helper.renderTextRun(fragment, ((TextRenderData) (content)));
        } else if (content instanceof PictureRenderData) {
            PictureRenderPolicy.Helper.renderPicture(fragment, ((PictureRenderData) (content)));
        } else if (content instanceof CommentRenderData) {
            CommentRenderPolicy.Helper.renderComment(fragment, ((CommentRenderData) (content)));
        }
    }
}

@Override
public void render(ElementTemplate eleTemplate, Object data, XWPFTemplate template) {
    RunTemplate runTemplate = ((RunTemplate) (eleTemplate));
    XWPFRun run = runTemplate.getRun();
    try {
        if (!TableTools.isInsideTable(run)) {
            throw new IllegalStateException(("The template tag " + runTemplate.getSource()) + " must be inside a table");
        }
        XWPFTableCell tagCell = ((XWPFTableCell) (((XWPFParagraph) (run.getParent())).getBody()));
        XWPFTable table = tagCell.getTableRow().getTable();
        run.setText("", 0);
        int templateColIndex = getTemplateColIndex(tagCell);
        int actualColIndex = getActualInsertPosition(tagCell.getTableRow(), templateColIndex);
        XWPFTableCell firstCell = tagCell.getTableRow().getCell(actualColIndex);
        int width = firstCell.getWidth();
        TableWidthType widthType = firstCell.getWidthType();
        if ((TableWidthType.DXA != widthType) || (width == 0)) {
            throw new IllegalArgumentException("template col must set width in centimeters.");
        }
        int rowSize = table.getRows().size();
        if ((null != data) && (data instanceof Iterable)) {
            int colWidth = processLoopColWidth(table, width, templateColIndex, data);
            Iterator<?> iterator = ((Iterable<?>) (data)).iterator();
            int insertPosition = templateColIndex;
            TemplateResolver resolver = new TemplateResolver(template.getConfig().copy(prefix, suffix));
            int index = 0;
            boolean hasNext = iterator.hasNext();
            while (hasNext) {
                Object root = iterator.next();
                hasNext = iterator.hasNext();
                insertPosition = templateColIndex++;
                List<XWPFTableCell> cells = new ArrayList<XWPFTableCell>();
                for (int i = 0; i < rowSize; i++) {
                    XWPFTableRow row = table.getRow(i);
                    int actualInsertPosition = getActualInsertPosition(row, insertPosition);
                    if ((-1) == actualInsertPosition) {
                        addColGridSpan(row, insertPosition);
                        continue;
                    }
                    XWPFTableCell templateCell = row.getCell(actualInsertPosition);
                    templateCell.setWidth(colWidth + "");
                    XWPFTableCell nextCell = insertCell(row, actualInsertPosition);
                    setTableCell(row, templateCell, actualInsertPosition);
                    // double set row
                    XmlCursor newCursor = templateCell.getCTTc().newCursor();
                    newCursor.toPrevSibling();
                    XmlObject object = newCursor.getObject();
                    nextCell = new XWPFTableCell(((CTTc) (object)), row, ((IBody) (nextCell.getPart())));
                    setTableCell(row, nextCell, actualInsertPosition);
                    cells.add(nextCell);
                }
                RenderDataCompute dataCompute = template.getConfig().getRenderDataComputeFactory().newCompute(EnvModel.of(root, EnvIterator.makeEnv(index++, hasNext)));
                cells.forEach(cell -> {
                    List<MetaTemplate> templates = resolver.resolveBodyElements(cell.getBodyElements());
                    new DocumentProcessor(template, resolver, dataCompute).process(templates);
                });
            } 
        }
        for (int i = 0; i < rowSize; i++) {
            XWPFTableRow row = table.getRow(i);
            int actualInsertPosition = getActualInsertPosition(row, templateColIndex);
            if ((-1) == actualInsertPosition) {
                minusGridSpan(row, templateColIndex);
                continue;
            }
            removeCell(row, actualInsertPosition);
        }
        afterloop(table, data);
    } catch (Exception e) {
        throw new RenderException((("HackLoopTable for " + eleTemplate) + "error: ") + e.getMessage(), e);
    }
}

public static void renderDocument(XWPFRun run, DocumentRenderData data) throws Exception {
    List<RenderData> contents = data.getContents();
    BodyContainer bodyContainer = BodyContainerFactory.getBodyContainer(run);
    for (RenderData item : contents) {
        XWPFParagraph paragraph = bodyContainer.insertNewParagraph(run);
        XWPFRun createRun = paragraph.createRun();
        StyleUtils.styleParagraph(paragraph, run.getParent());
        StyleUtils.styleRun(createRun, run);
        if (item instanceof ParagraphRenderData) {
            ParagraphRenderPolicy.Helper.renderParagraph(createRun, ((ParagraphRenderData) (item)));
        } else if (item instanceof TableRenderData) {
            TableRenderPolicy.Helper.renderTable(createRun, ((TableRenderData) (item)));
            BodyContainerFactory.getBodyContainer(createRun).clearPlaceholder(createRun);
        } else if (item instanceof NumberingRenderData) {
            NumberingRenderPolicy.Helper.renderNumbering(createRun, ((NumberingRenderData) (item)));
            BodyContainerFactory.getBodyContainer(createRun).clearPlaceholder(createRun);
        }
    }
}

