@Override
public XWPFTable insertNewTable(XWPFRun run, int row, int col) {
    XmlCursor cursor = ((XWPFParagraph) (run.getParent())).getCTP().newCursor();
    XWPFTable table = insertNewTbl(cursor);
    // hack for cursor.removeXmlContents(); in XWPFHeaderFooter
    List<XWPFTableRow> rows = table.getRows();
    for (int i = 0; i < rows.size(); i++) {
        table.removeRow(i);
    }
    for (int i = 0; i < row; i++) {
        XWPFTableRow tabRow = (table.getRow(i) == null) ? table.createRow() : table.getRow(i);
        for (int k = 0; k < col; k++) {
            if (tabRow.getCell(k) == null) {
                tabRow.createCell();
            }
        }
    }
    return table;
}

public static NumberingContinue of(BodyContainer bodyContainer, int start, int end, IterableTemplate iterable) {
    if ((start + 1) >= end)
        return new NumberingContinue();

    final List<IBodyElement> elements = bodyContainer.getBodyElements().subList(start + 1, end);
    if (elements.isEmpty())
        return new NumberingContinue();

    CTNumPr first = null;
    int firstPos = -1;
    for (IBodyElement element : elements) {
        if (element.getElementType() == BodyElementType.PARAGRAPH) {
            XWPFParagraph paragraph = ((XWPFParagraph) (element));
            CTP ctp = paragraph.getCTP();
            if ((ctp.getPPr() != null) && (ctp.getPPr().getNumPr() != null)) {
                CTNumPr numPr = ctp.getPPr().getNumPr();
                // find first
                if (null == first) {
                    first = numPr;
                    firstPos = bodyContainer.getPosOfParagraphCTP(ctp);
                } else // first is not unique
                if (Objects.equals(numPr.getIlvl().getVal(), first.getIlvl().getVal()) && Objects.equals(numPr.getNumId().getVal(), first.getNumId().getVal())) {
                    first = null;
                    break;
                }
            }
        }
    }
    if (null == first)
        return new NumberingContinue();

    // the first is unique, if first inside other iterable section
    List<MetaTemplate> templates = iterable.getTemplates();
    for (MetaTemplate template : templates) {
        if (template instanceof IterableTemplate) {
            CTP startCtp = ((XWPFParagraph) (((IterableTemplate) (template)).getStartRun().getParent())).getCTP();
            CTP endCtp = ((XWPFParagraph) (((IterableTemplate) (template)).getEndRun().getParent())).getCTP();
            int startPos = bodyContainer.getPosOfParagraphCTP(startCtp);
            if (startPos >= firstPos)
                break;

            int endPos = bodyContainer.getPosOfParagraphCTP(endCtp);
            if ((firstPos > startPos) && (firstPos < endPos)) {
                return new NumberingContinue();
            }
        }
    }
    return new NumberingContinue(first.getNumId().getVal());
}

@Override
public void render(ElementTemplate eleTemplate, Object data, XWPFTemplate template) {
    try {
        RunTemplate runTemplate = cast2runTemplate(eleTemplate);
        XWPFRun run = runTemplate.getRun();
        checkTargetIsTable(run, ("Processing [" + runTemplate.getTagName()) + "] failed, the target content is not a table");
        XWPFTableCell tagCell = ((XWPFTableCell) (((XWPFParagraph) (run.getParent())).getBody()));
        final XWPFTable table = tagCell.getTableRow().getTable();
        run.setText("", 0);
        TemplateResolver resolver = new TemplateResolver(template.getConfig().copy(prefix, suffix));
        // 
        int position = getRowIndex(tagCell.getTableRow());
        List<XWPFTableRow> tempRows = getAllTemplateRow(table, position);
        if ((null != data) && (data instanceof Iterable)) {
            // 
            final XWPFTableRow firstTempRow = tempRows.get(0);
            Iterator<?> dataIt = ((Iterable<?>) (data)).iterator();
            boolean hasNextData = dataIt.hasNext();
            int index = 0;
            while (hasNextData) {
                Object dt = dataIt.next();
                hasNextData = dataIt.hasNext();
                Iterator<XWPFTableRow> rowTempIt = tempRows.iterator();
                boolean hasNextTempRow = rowTempIt.hasNext();
                while (hasNextTempRow) {
                    XWPFTableRow tempRow = rowTempIt.next();
                    hasNextTempRow = rowTempIt.hasNext();
                    if (!table.addRow(tempRow, position)) {
                        throw new RenderException("");
                    }
                    // 
                    XmlCursor newCursor = firstTempRow.getCtRow().newCursor();
                    newCursor.toPrevSibling();
                    XmlObject object = newCursor.getObject();
                    XWPFTableRow newRow = new XWPFTableRow(((CTRow) (object)), table);
                    newRow.getCtRow().set(object);
                    setTableRow(table, newRow, position);
                    List<XWPFTableCell> cells = newRow.getTableCells();
                    RenderDataCompute dataCompute = template.getConfig().getRenderDataComputeFactory().newCompute(EnvModel.of(dt, EnvIterator.makeEnv(index, hasNextData || hasNextTempRow)));
                    cells.forEach(tableCell -> {
                        List<MetaTemplate> metaTemplates = resolver.resolveBodyElements(tableCell.getBodyElements());
                        new DocumentProcessor(template, resolver, dataCompute).process(metaTemplates);
                    });
                    ++position;
                } 
                ++index;
            } 
        }
        removeTableRow(table, position, tempRows.size());
    } catch (Exception e) {
        throw new RenderException("failed to render table multi-row template", e);
    }
}

